import { useState, useEffect } from "react";
import { Form, Row, Col, Card, Spinner } from "react-bootstrap";
import { Link } from "react-router-dom";
import axios from "axios";
import { useAuth } from "../context/AuthContext";

const Search = () => {
  const [query, setQuery] = useState("");
  const [movies, setMovies] = useState([]); // Initialize as empty array
  const [loading, setLoading] = useState(false);
  const { user } = useAuth();

  useEffect(() => {
    const searchMovies = async () => {
      setLoading(true);
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get(`/api/movies/search?query=${query}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        // Ensure we always set an array
        setMovies(response.data || []);
      } catch (err) {
        console.error("Search error:", err);
        setMovies([]); // Reset to empty array on error
      } finally {
        setLoading(false);
      }
    };

    const timer = setTimeout(() => {
      if (query.trim()) {
        searchMovies();
      } else {
        setMovies([]);
      }
    }, 500);

    return () => clearTimeout(timer);
  }, [query]);

  return (
    <div className="p-4">
      <Form className="mb-4">
        <Form.Control
          size="lg"
          type="search"
          placeholder="Search for movies..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
      </Form>

      {loading && <Spinner animation="border" className="d-block mx-auto" />}

      {!loading && movies.length === 0 && query && (
        <div className="text-center text-muted mb-4">
          No movies found for "<strong>{query}</strong>"
        </div>
      )}

      <Row xs={1} md={4} className="g-4">
        {movies?.map(
          (
            movie // Added optional chaining
          ) => (
            <Col key={movie.id}>
              <Card className="h-100 shadow-sm">
                <Card.Img
                  variant="top"
                  src={
                    movie.posterPath
                      ? `https://image.tmdb.org/t/p/w500${movie.posterPath}`
                      : "/placeholder.jpg"
                  }
                />
                <Card.Body>
                  <Card.Title>{movie.title}</Card.Title>
                  <Card.Text className="text-muted small">
                    {movie.releaseDate} â€¢ {movie.genres?.join(", ")}
                  </Card.Text>
                  <Link
                    to={`/movie/${movie.id}`}
                    className="btn btn-primary w-100"
                  >
                    View Details
                  </Link>
                </Card.Body>
              </Card>
            </Col>
          )
        )}
      </Row>
    </div>
  );
};

export default Search;
