import React, { useEffect, useState } from "react";
import {
  ListGroup,
  Card,
  Spinner,
  Row,
  Col,
  Alert,
  Badge,
} from "react-bootstrap";
import { Link } from "react-router-dom";
import axios from "axios";
export default function Profile() {
  const [user, setUser] = useState({});
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [watchlist, setWatchlistData] = useState([]);

  const getWatchList = async () => {
    setLoading(true);
    try {
      let token = localStorage.getItem("tokenMovie") || null;
      if (!token) {
        const response = await axios.post("/api/auth/reqToken");
        console.log("Token response:", response.data);
        localStorage.setItem("tokenMovie", response.data.token);
        token = response.data.token;
      }
      const options = {
        method: "GET",
        headers: {
          accept: "application/json",
          Authorization: `Bearer ${token}`,
        },
      };
      const userId = JSON.parse(localStorage.getItem("user")).id;

      console.log("userId", userId);

      fetch(
        `https://api.themoviedb.org/3/account/${userId}/watchlist/movies?language=en-US&page=1&sort_by=created_at.asc`,
        options
      )
        .then((res) => res.json())
        .then((res) => {
          console.log(res);
          setUser(JSON.parse(localStorage.getItem("user")));
          getMovieGenre(options, res);
          if (localStorage.getItem("reviewed_movies")) {
            fetchReviewedMovie(
              JSON.parse(localStorage.getItem("reviewed_movies")),
              options,
              userId
            );
          }
          setLoading(false);
        })
        .catch((err) => console.error(err));
    } catch (err) {
      console.error("Search error:", err);
      setWatchlistData([]);
    } finally {
      setLoading(false);
    }
  };

  const getMovieGenre = async (options, moviesList) => {
    try {
      fetch(
        "https://api.themoviedb.org/3/genre/movie/list?language=en",
        options
      )
        .then((res) => res.json())
        .then((resGenre) => {
          // map res.results to check genre
          moviesList.results.map((movie) => {
            let genresMovie = [];
            movie.genre_ids.map((genreId) => {
              resGenre.genres.map((genreItem) => {
                if (genreItem.id === genreId) {
                  genresMovie.push(genreItem.name);
                }
              });
            });
            movie.genres = genresMovie;
          });
          setWatchlistData(moviesList.results || []);
        })
        .catch((err) => console.error(err));
    } catch (err) {
      console.error(err);
    }
  };

  const removeWatchList = async (movieId) => {
    try {
      const token = localStorage.getItem("tokenMovie");
      const userId = JSON.parse(localStorage.getItem("user")).id;
      console.log("userId", userId);

      const optionsPost = {
        method: "POST",
        headers: {
          accept: "application/json",
          "content-type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          media_type: "movie",
          media_id: movieId,
          watchlist: false,
        }),
      };

      fetch(
        `https://api.themoviedb.org/3/account/${userId}/watchlist`,
        optionsPost
      )
        .then((res) => res.json())
        .then((res) => {
          getWatchList();
        })
        .catch((err) => {
          console.log(err);
          getWatchList();
        });
    } catch (err) {
      console.error(err);
    }
  };

  const fetchReviewedMovie = async (reviewedMovies, options, userId) => {
    let movieData = [];
    await reviewedMovies.map(async (movie) => {
      if (movie.userid === userId) {
        await fetch(
          `https://api.themoviedb.org/3/movie/${movie.id}?language=en-US`,
          options
        )
          .then((res) => res.json())
          .then((res) => {
            res.review = movie.review;
            res.rating = movie.rating;
            res.comment = movie.review;
            movieData.push(res);
            console.log("reviewwww", res);
          })
          .catch((err) => console.error(err));
      }
    });
    setReviews(movieData);
  };
  useEffect(() => {
    getWatchList();
  }, []);

  if (loading)
    return (
      <div className="d-flex justify-content-center align-items-center min-vh-100">
        <Spinner animation="border" variant="primary" />
      </div>
    );
  return (
    <div className="container mt-4 mb-5">
      {error && (
        <Alert variant="danger" className="rounded-0 shadow">
          {error}
        </Alert>
      )}


     {/* User Profile Card */}
     <Card className="mb-4 shadow-sm border-0">
        <Card.Body className="p-4">
          <div className="d-flex align-items-center gap-4">
            <div className="bg-primary rounded-circle d-flex align-items-center justify-content-center" style={{ width: '80px', height: '80px' }}>
              <span className="text-white h4 mb-0">{user.fullName[0]}</span>
            </div>
            <div>
              <Card.Title className="h3 mb-1" style={{ textTransform: "capitalize" }}>
                {user.fullName}
              </Card.Title>
              <div className="text-muted small">
                <div>@{user.username}</div>
                <div>{user.email}</div>
              </div>
              <Badge bg="secondary" className="mt-2">
                Watchlist: {watchlist.length}
              </Badge>
            </div>
          </div>
        </Card.Body>
      </Card>

 {/* Reviews Section */}
      <section className="mb-5">
        <h3 className="mb-4 border-bottom pb-2">My Reviews</h3>
        {reviews.length === 0 ? (
          <div className="text-center py-4 bg-light rounded">
            <span className="text-muted">No reviews submitted yet</span>
          </div>
        ) : (
          <Row xs={1} lg={2} className="g-4">
            {reviews.map((review) => (
              <Col key={review.title}>
                <Card className="h-100 shadow-sm">
                  <div className="d-flex g-0">
                    <div style={{ width: '150px' }}>
                      <Card.Img
                        variant="left"
                        src={
                          review.poster_path
                            ? `https://image.tmdb.org/t/p/w500${review.poster_path}`
                            : "/placeholder.jpg"
                        }
                        className="h-100 rounded-start"
                        style={{ objectFit: 'cover' }}
                      />
                    </div>
                    <Card.Body className="p-3">
                      <h5 className="mb-2">
                        <Link to={`/movie/${review.id}`} className="text-decoration-none">
                          {review.title}
                        </Link>
                      </h5>
                      <div className="d-flex align-items-center mb-2">
                        <div className="text-warning fs-5">
                          {"★".repeat(review.rating)}
                          {"☆".repeat(5 - review.rating)}
                        </div>
                        <span className="ms-2 text-muted small">({review.rating}/5)</span>
                      </div>
                      <p className="text-muted mb-2">{review.comment}</p>
                      {review.genres && (
                        <div className="d-flex flex-wrap gap-1">
                          {review.genres.map((genre, index) => (
                            <Badge key={index} bg="light" text="dark" className="border">
                              {genre}
                            </Badge>
                          ))}
                        </div>
                      )}
                    </Card.Body>
                  </div>
                </Card>
              </Col>
            ))}
          </Row>
        )}
      </section>

      {/* Watchlist Section */}
      <section>
        <h3 className="mb-4 border-bottom pb-2">My Watchlist</h3>
        {watchlist.length === 0 ? (
          <div className="text-center py-4 bg-light rounded">
            <span className="text-muted">Your watchlist is currently empty</span>
          </div>
        ) : (
          <Row xs={2} md={4} lg={5} className="g-4">
            {watchlist.map((movie) => (
              <Col key={movie.id}>
                <Card className="h-100 shadow-sm border-0">
                  <div className="position-relative">
                    <Card.Img
                      variant="top"
                      src={
                        movie.poster_path
                          ? `https://image.tmdb.org/t/p/w500${movie.poster_path}`
                          : "/placeholder.jpg"
                      }
                      style={{ height: '300px', objectFit: 'cover' }}
                      className="rounded-top"
                    />
                    <Badge bg="danger" className="position-absolute top-0 end-0 m-2">
                      {movie.vote_average?.toFixed(1)}
                    </Badge>
                  </div>
                  <Card.Body className="d-flex flex-column">
                    <Card.Title className="h6 mb-2">{movie.title}</Card.Title>
                    <div className="mt-auto">
                      <small className="text-muted d-block mb-2">
                        {movie.release_date && new Date(movie.release_date).getFullYear()}
                        {movie.genres && ` • ${movie.genres.slice(0, 2).join(', ')}`}
                      </small>
                      <Link
                        to={`/movie/${movie.id}`}
                        className="btn btn-sm btn-outline-primary w-100"
                      >
                        Details
                      </Link>
                      <button
                        onClick={() => removeWatchList(movie.id)}
                        className="btn btn-sm btn-outline-danger w-100 mt-2"
                      >
                        Remove
                      </button>
                    </div>
                  </Card.Body>
                </Card>
              </Col>
            ))}
          </Row>
        )}
      </section>
    </div>
  );
}
