import React, { useState, useEffect } from "react";
import { Form, Button, Alert, Card, Spinner } from "react-bootstrap";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

const Login = () => {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });
  const [error, setError] = useState("");
  const [serverError, setServerError] = useState(false);
  const [loading, setLoading] = useState(false);
  const [attempts, setAttempts] = useState(0);
  const { login } = useAuth(); // Get login function from context
  const navigate = useNavigate();

  // Reset server error state when user modifies form
  useEffect(() => {
    if (serverError) {
      setServerError(false);
    }
  }, [formData]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setLoading(true);

    try {
      const response = await login(formData.email, formData.password);

      if (response.success) {
        navigate("/");
      } else {
        // Increment attempt counter
        setAttempts(prev => prev + 1);
        
        // Check if error is server-related
        if (response.status >= 500 || response.message?.includes('server')) {
          setServerError(true);
          setError("We're experiencing technical difficulties. Please try again later or contact support.");
        } else if (response.status === 401 || response.status === 403) {
          setError("Invalid email or password. Please try again.");
        } else {
          setError(response.error || "Login failed. Please check your credentials.");
        }
      }
    } catch (err) {
      setAttempts(prev => prev + 1);
      setServerError(true);
      setError("Unable to connect to the server. Please check your internet connection and try again.");
      console.error("Login error:", err);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const retryConnection = () => {
    setServerError(false);
    setError("");
    handleSubmit(new Event('submit'));
  };

  return (
    <div className="d-flex align-items-center min-vh-100">
      <Card className="w-100 mx-auto shadow-sm" style={{ maxWidth: "400px" }}>
        <Card.Body className="p-4">
          <h2 className="text-center mb-4">Login</h2>
          
          {/* Server Error Display */}
          {serverError ? (
            <Alert variant="danger">
              <Alert.Heading className="fs-5">Server Connection Error</Alert.Heading>
              <p>{error}</p>
              <div className="d-flex justify-content-end">
                <Button 
                  variant="outline-danger" 
                  size="sm"
                  onClick={retryConnection}
                  disabled={loading}
                >
                  {loading ? (
                    <>
                      <Spinner as="span" animation="border" size="sm" role="status" aria-hidden="true" />
                      <span className="ms-2">Retrying...</span>
                    </>
                  ) : (
                    <>Retry Connection</>
                  )}
                </Button>
              </div>
            </Alert>
          ) : (
            error && <Alert variant="danger">{error}</Alert>
          )}

          {/* Too many attempts warning */}
          {attempts >= 3 && !serverError && (
            <Alert variant="warning">
              <i className="bi bi-exclamation-triangle me-2"></i>
              Having trouble logging in? Make sure your caps lock is off, or try resetting your password.
            </Alert>
          )}

          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                name="email"
                required
                value={formData.email}
                onChange={handleChange}
                placeholder="Enter email"
                className={error && error.includes("email") ? "is-invalid" : ""}
                disabled={loading}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="password"
                name="password"
                required
                value={formData.password}
                onChange={handleChange}
                placeholder="Enter password"
                className={error && error.includes("password") ? "is-invalid" : ""}
                disabled={loading}
              />
              <div className="d-flex justify-content-end mt-1">
                <Link to="/forgot-password" className="text-decoration-none small">
                  Forgot password?
                </Link>
              </div>
            </Form.Group>

            <Button
              variant="primary"
              type="submit"
              className="w-100 mb-3"
              disabled={loading || (serverError && attempts > 2)}
            >
              {loading ? (
                <>
                  <Spinner
                    as="span"
                    animation="border"
                    size="sm"
                    role="status"
                    aria-hidden="true"
                  />
                  <span className="ms-2">Logging in...</span>
                </>
              ) : (
                "Login"
              )}
            </Button>

            <div className="text-center">
              Don't have an account?{" "}
              <Link to="/register" className="text-decoration-none">
                Register
              </Link>
            </div>
            
            {/* Help section that appears after multiple failed attempts */}
            {attempts >= 3 && (
              <div className="mt-4 border-top pt-3">
                <h6>Need Help?</h6>
                <ul className="small text-muted ps-3">
                  <li>Make sure you're using the correct email address</li>
                  <li>Passwords are case-sensitive</li>
                  <li>Check your internet connection</li>
                  <li>If problems persist, contact <a href="mailto:support@example.com">support</a></li>
                </ul>
              </div>
            )}
          </Form>
        </Card.Body>
      </Card>
    </div>
  );
};

export default Login;
