import { Container } from "react-bootstrap";
import { useState, useEffect } from "react";
const Home = () => {
  const [query, setQuery] = useState("");
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const searchMovies = async () => {
      setLoading(true);
      try {
        const token = localStorage.getItem("token");
        const options = {
          method: "GET",
          headers: {
            accept: "application/json",
            Authorization: `Bearer ${token}`,
          },
        };
        let fetchUri =
          query.length < 1
            ? "discover/movie?include_adult=false&include_video=false&language=en-US&page=1&sort_by=popularity.desc"
            : `search/movie?query=${query}&include_adult=false&language=en-US&page=1`;
        fetch(`https://api.themoviedb.org/3/${fetchUri}`, options)
          .then((res) => res.json())
          .then((res) => {
            console.log(res);
            setMovies(res.results || []);
          })
          .catch((err) => console.error(err));
      } catch (err) {
        console.error("Search error:", err);
        setMovies([]);
      } finally {
        setLoading(false);
      }
    };

    const timer = setTimeout(searchMovies, 500);
    return () => clearTimeout(timer);
  }, [query]);

  return (
    <div className="p-4">
       {loading && <Spinner animation="border" className="d-block mx-auto" />}

{!loading && movies.length === 0 && query.length >= 3 && (
  <div className="text-center text-muted mb-4">
    No movies found for "{query}"
  </div>
)}

<Row xs={1} md={4} className="g-4">
  {movies?.map((movie) => (
    <Col key={movie.id}>
      <Card className="h-100 shadow-sm">
        <Card.Img
          variant="top"
          src={
            movie.poster_path
              ? `https://image.tmdb.org/t/p/w500${movie.poster_path}`
              : "/placeholder.jpg"
          }
          style={{ height: "400px", objectFit: "cover" }}
        />
        <Card.Body className="d-flex flex-column">
          <Card.Title>{movie.title}</Card.Title>
          <Card.Text className="text-muted small mt-auto">
            {movie.release_date &&
              new Date(movie.release_date).getFullYear()}
            {movie.vote_average &&
              ` • ⭐ ${movie.vote_average.toFixed(1)}`}
          </Card.Text>
          <Link
            to={`/movie/${movie.id}`}
            className="btn btn-primary w-100 mt-2"
          >
            View Details
          </Link>
        </Card.Body>
      </Card>
    </Col>
  ))}
</Row>
      <Container className="text-center mt-5">
        <h1>Welcome to Cinerate</h1>
        <p className="lead">Discover, rate, and review your favorite movies</p>
      </Container>
    </div>
  );
};

export default Home;
