import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Form, Row, Col, Card, Spinner } from "react-bootstrap";
import axios from "axios";

const Search = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  console.log("test search");
  useEffect(() => {
    console.log("test search");
    const searchMovies = async () => {
      if (searchTerm.length < 3) {
        setMovies([]);
        return;
      }

      setLoading(true);
      try {
        const response = await axios.get(
          `/api/movies/search?query=${searchTerm}`
        );
        setMovies(response.data);
      } catch (err) {
        console.error("Search error:", err);
      } finally {
        setLoading(false);
      }
    };

    const debounceTimer = setTimeout(() => {
      searchMovies();
    }, 500);

    return () => clearTimeout(debounceTimer);
  }, [searchTerm]);

  return (
    <div className="p-4">
      <Form className="mb-4">
        <Form.Control
          type="search"
          placeholder="Search for movies..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          size="lg"
        />
      </Form>

      {loading && (
        <div className="text-center">
          <Spinner animation="border" />
        </div>
      )}

      {!loading && movies.length === 0 && searchTerm.length >= 3 && (
        <div className="text-muted text-center">No movies found</div>
      )}

      <Row xs={1} md={3} lg={4} className="g-4">
        {movies.map((movie) => (
          <Col key={movie.tmdbId || movie.id}>
            <Card
              className="h-100 shadow-sm cursor-pointer"
              onClick={() => navigate(`/movies/${movie.tmdbId || movie.id}`)}
            >
              <Card.Img
                variant="top"
                src={
                  movie.posterPath
                    ? `https://image.tmdb.org/t/p/w500${movie.posterPath}`
                    : "/placeholder-movie.jpg"
                }
                style={{ height: "400px", objectFit: "cover" }}
              />
              <Card.Body>
                <Card.Title>{movie.title}</Card.Title>
                <Card.Text className="text-muted small">
                  {movie.releaseDate &&
                    new Date(movie.releaseDate).getFullYear()}
                  {movie.rating && ` • ⭐ ${movie.rating.toFixed(1)}`}
                </Card.Text>
                <Card.Text className="text-truncate small">
                  {movie.overview}
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </div>
  );
};

export default Search;
