import React, { useEffect, useState } from "react";
import {
  ListGroup,
  Card,
  Spinner,
  Row,
  Col,
  Alert,
  Badge,
  Container,
} from "react-bootstrap";
import { Link } from "react-router-dom";
import axios from "axios";

export default function Profile() {
  const [user, setUser] = useState({});
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [watchlist, setWatchlistData] = useState([]);

  const getWatchList = async () => {
    setLoading(true);
    try {
      let token = localStorage.getItem("tokenMovie") || null;
      if (!token) {
        const response = await axios.post("/api/auth/reqToken");
        console.log("Token response:", response.data);
        localStorage.setItem("tokenMovie", response.data.token);
        token = response.data.token;
      }
      const options = {
        method: "GET",
        headers: {
          accept: "application/json",
          Authorization: `Bearer ${token}`,
        },
      };
      const userId = JSON.parse(localStorage.getItem("user")).id;

      console.log("userId", userId);

      fetch(
        `https://api.themoviedb.org/3/account/${userId}/watchlist/movies?language=en-US&page=1&sort_by=created_at.asc`,
        options
      )
        .then((res) => res.json())
        .then((res) => {
          console.log(res);
          setUser(JSON.parse(localStorage.getItem("user")));
          getMovieGenre(options, res);
          if (localStorage.getItem("reviewed_movies")) {
            fetchReviewedMovie(
              JSON.parse(localStorage.getItem("reviewed_movies")),
              options,
              userId
            );
          }
          setLoading(false);
        })
        .catch((err) => console.error(err));
    } catch (err) {
      console.error("Search error:", err);
      setWatchlistData([]);
    } finally {
      setLoading(false);
    }
  };

  const getMovieGenre = async (options, moviesList) => {
    try {
      fetch(
        "https://api.themoviedb.org/3/genre/movie/list?language=en",
        options
      )
        .then((res) => res.json())
        .then((resGenre) => {
          // map res.results to check genre
          moviesList.results.map((movie) => {
            let genresMovie = [];
            movie.genre_ids.map((genreId) => {
              resGenre.genres.map((genreItem) => {
                if (genreItem.id === genreId) {
                  genresMovie.push(genreItem.name);
                }
              });
            });
            movie.genres = genresMovie;
          });
          setWatchlistData(moviesList.results || []);
        })
        .catch((err) => console.error(err));
    } catch (err) {
      console.error(err);
    }
  };

  const removeWatchList = async (movieId) => {
    try {
      const token = localStorage.getItem("tokenMovie");
      const userId = JSON.parse(localStorage.getItem("user")).id;
      console.log("userId", userId);

      const optionsPost = {
        method: "POST",
        headers: {
          accept: "application/json",
          "content-type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          media_type: "movie",
          media_id: movieId,
          watchlist: false,
        }),
      };

      fetch(
        `https://api.themoviedb.org/3/account/${userId}/watchlist`,
        optionsPost
      )
        .then((res) => res.json())
        .then((res) => {
          getWatchList();
        })
        .catch((err) => {
          console.log(err);
          getWatchList();
        });
    } catch (err) {
      console.error(err);
    }
  };

  const fetchReviewedMovie = async (reviewedMovies, options, userId) => {
    let movieData = [];
    await reviewedMovies.map(async (movie) => {
      if (movie.userid === userId) {
        await fetch(
          `https://api.themoviedb.org/3/movie/${movie.id}?language=en-US`,
          options
        )
          .then((res) => res.json())
          .then((res) => {
            res.review = movie.review;
            res.rating = movie.rating;
            res.comment = movie.review;
            movieData.push(res);
            console.log("reviewwww", res);
          })
          .catch((err) => console.error(err));
      }
    });
    setReviews(movieData);
  };
  useEffect(() => {
    getWatchList();
  }, []);

  if (loading)
    return (
      <div
        className="d-flex justify-content-center align-items-center"
        style={{ height: "100vh", background: "#f8f9fa" }}
      >
        <div className="text-center">
          <Spinner
            animation="border"
            variant="primary"
            style={{ width: "3.5rem", height: "3.5rem" }}
          />
          <p className="mt-3 text-primary fw-bold">Loading your profile...</p>
        </div>
      </div>
    );


  return (
    <div style={{ backgroundColor: "#f8f9fa", minHeight: "100vh", paddingBottom: "3rem" }}>
      <Container className="pt-5">
        {error && (
          <Alert variant="danger" className="shadow-sm mb-4">
            {error}
          </Alert>
        )}

        {/* Top User Profile Banner with Gradient */}
        <Card className="mb-4 border-0 overflow-hidden profile-header">
          <div className="profile-banner position-relative">
            <div className="position-absolute w-100 h-100" 
                 style={{ 
                   backgroundImage: "linear-gradient(135deg, #4158D0 0%, #C850C0 46%, #FFCC70 100%)",
                   opacity: 0.9
                 }}>
            </div>
            <Card.Body className="p-5 position-relative text-center text-white">
              <div
                className="bg-white shadow-lg text-primary rounded-circle d-flex justify-content-center align-items-center mx-auto mb-4"
                style={{ 
                  width: "130px", 
                  height: "130px", 
                  fontSize: "3.5rem",
                  border: "4px solid white"
                }}
              >
                {user.fullName ? user.fullName.charAt(0).toUpperCase() : "U"}
              </div>
              <h1 className="display-4 fw-bold mb-1" style={{ textTransform: "capitalize" }}>
                {user.fullName}
              </h1>
              <p className="fs-5 mb-0">@{user.username}</p>
              <div className="email-badge mt-3">
                <span className="px-3 py-1 bg-white bg-opacity-25 rounded-pill">
                  <i className="bi bi-envelope me-2"></i> {user.email}
                </span>
              </div>
            </Card.Body>
          </div>
        </Card>

        {/* Stats Card */}
        <Card className="mb-5 border-0 shadow-sm">
          <Card.Body className="p-0">
            <Row className="g-0">
              <Col md={4} className="stats-col">
                <div className="text-center p-4 h-100 d-flex flex-column justify-content-center stat-item">
                  <div className="stat-icon mb-3">
                    <div className="icon-circle bg-primary bg-opacity-10 mx-auto">
                      <i className="bi bi-calendar-check fs-2 text-primary"></i>
                    </div>
                  </div>
                  <h4 className="fw-bold">Member Since</h4>
                  <p className="mb-0 fs-5">{memberSince}</p>
                </div>
              </Col>
              <Col md={4} className="stats-col">
                <div className="text-center p-4 h-100 d-flex flex-column justify-content-center stat-item">
                  <div className="stat-icon mb-3">
                    <div className="icon-circle bg-warning bg-opacity-10 mx-auto">
                      <i className="bi bi-star fs-2 text-warning"></i>
                    </div>
                  </div>
                  <h4 className="fw-bold">Reviews</h4>
                  <p className="mb-0 fs-5">{reviews.length}</p>
                </div>
              </Col>
              <Col md={4} className="stats-col">
                <div className="text-center p-4 h-100 d-flex flex-column justify-content-center stat-item">
                  <div className="stat-icon mb-3">
                    <div className="icon-circle bg-info bg-opacity-10 mx-auto">
                      <i className="bi bi-film fs-2 text-info"></i>
                    </div>
                  </div>
                  <h4 className="fw-bold">Watchlist</h4>
                  <p className="mb-0 fs-5">{watchlist.length}</p>
                </div>
              </Col>
            </Row>
          </Card.Body>
        </Card>

        {/* Reviews Section */}
        <Card className="border-0 shadow-sm mb-5">
          <Card.Header className="bg-white p-4 border-0">
            <div className="d-flex align-items-center">
              <h3 className="mb-0 me-2 fw-bold">My Reviews</h3>
              <Badge bg="primary" pill className="px-3 py-2 fs-6">
                {reviews.length}
              </Badge>
            </div>
          </Card.Header>
          <Card.Body className="p-4">
            {reviews.length === 0 ? (
              <div className="text-center py-5">
                <i className="bi bi-journal-text fs-1 text-muted mb-3"></i>
                <p className="text-muted mb-0 fs-5">
                  You haven't written any reviews yet
                </p>
              </div>
            ) : (
              <div className="review-list">
                {reviews.map((review, index) => (
                  <div 
                    key={review.title} 
                    className={`review-item p-4 ${index !== reviews.length - 1 ? 'border-bottom' : ''}`}
                  >
                    <div className="d-flex">
                      <div className="me-4" style={{ width: "120px" }}>
                        <Card.Img
                          className="rounded shadow-sm"
                          src={
                            review.poster_path
                              ? `https://image.tmdb.org/t/p/w500${review.poster_path}`
                              : "/placeholder.jpg"
                          }
                          alt={review.title}
                        />
                      </div>
                      <div className="flex-grow-1">
                        <h4 className="mb-2 fw-bold">
                          <Link
                            to={`/movie/${review.id}`}
                            className="text-decoration-none text-primary"
                          >
                            {review.title}
                          </Link>
                        </h4>
                        {review.genres && (
                          <div className="mb-3">
                            {review.genres.map((genre) => (
                              <Badge
                                bg="light"
                                text="dark"
                                className="me-2 px-3 py-2"
                                key={genre.name}
                              >
                                {genre.name}
                              </Badge>
                            ))}
                          </div>
                        )}
                        <div className="text-warning mb-3 fs-4">
                          {"★".repeat(review.rating)}
                          {"☆".repeat(5 - review.rating)}
                        </div>
                        <p className="mb-0 text-muted fs-5">{review.comment}</p>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </Card.Body>
        </Card>

        {/* Watchlist Section */}
        <Card className="border-0 shadow-sm mb-4">
          <Card.Header className="bg-white p-4 border-0">
            <div className="d-flex align-items-center">
              <h3 className="mb-0 me-2 fw-bold">My Watchlist</h3>
              <Badge bg="primary" pill className="px-3 py-2 fs-6">
                {watchlist.length}
              </Badge>
            </div>
          </Card.Header>
          <Card.Body className="p-4">
            {watchlist.length === 0 ? (
              <div className="text-center py-5">
                <i className="bi bi-film fs-1 text-muted mb-3"></i>
                <p className="text-muted mb-0 fs-5">Your watchlist is empty</p>
              </div>
            ) : (
              <Row xs={1} md={2} lg={4} className="g-4">
                {watchlist.map((movie) => (
                  <Col key={movie.id}>
                    <Card className="h-100 border-0 shadow-sm hover-card">
                      <div className="position-relative movie-poster-container">
                        <Card.Img
                          variant="top"
                          src={
                            movie.poster_path
                              ? `https://image.tmdb.org/t/p/w500${movie.poster_path}`
                              : "/placeholder.jpg"
                          }
                          className="movie-poster"
                          style={{ height: "300px", objectFit: "cover" }}
                          alt={movie.title}
                        />
                        {movie.vote_average && (
                          <span className="position-absolute top-0 end-0 rating-badge">
                            ⭐{" "}
                            {movie.vote_average > 5
                              ? 5
                              : movie.vote_average.toFixed(1)}
                          </span>
                        )}
                      </div>
                      <Card.Body className="d-flex flex-column">
                        <Card.Title
                          className="fw-bold mb-2"
                          style={{
                            minHeight: "48px",
                            display: "-webkit-box",
                            WebkitLineClamp: "2",
                            WebkitBoxOrient: "vertical",
                            overflow: "hidden",
                          }}
                        >
                          {movie.title}
                        </Card.Title>
                        <Card.Text className="text-muted small mb-2">
                          {movie.release_date &&
                            new Date(movie.release_date).getFullYear()}
                        </Card.Text>
                        <div
                          className="mb-3"
                          style={{
                            minHeight: "60px",
                            display: "flex",
                            flexWrap: "wrap",
                            alignContent: "flex-start",
                          }}
                        >
                          {movie.genres &&
                            movie.genres.map((genre, index) => (
                              <span
                                key={index}
                                className="badge bg-light text-dark me-1 mb-1"
                              >
                                {genre}
                              </span>
                            ))}
                        </div>
                        <div className="d-grid gap-2 mt-auto">
                          <Link
                            to={`/movie/${movie.id}`}
                            className="btn btn-primary"
                          >
                            View Details
                          </Link>
                          <button
                            onClick={() => removeWatchList(movie.id)}
                            className="btn btn-outline-danger"
                          >
                            Remove
                          </button>
                        </div>
                      </Card.Body>
                    </Card>
                  </Col>
                ))}
              </Row>
            )}
          </Card.Body>
        </Card>
      </Container>

      <style jsx="true">{`
        .profile-header {
          border-radius: 16px;
          overflow: hidden;
          box-shadow: 0 8px 30px rgba(0, 0, 0, 0.12);
        }
        
        .profile-banner {
          min-height: 300px;
        }
        
        .stats-col + .stats-col {
          border-left: 1px solid rgba(0, 0, 0, 0.08);
        }
        
        .icon-circle {
          width: 70px;
          height: 70px;
          border-radius: 50%;
          display: flex;
          align-items: center;
          justify-content: center;
        }
        
        .stat-item:hover {
          background-color: rgba(0, 0, 0, 0.02);
          transition: background-color 0.3s ease;
        }
        
        .hover-card {
          transition: all 0.3s ease;
          border-radius: 12px;
          overflow: hidden;
        }
        
        .hover-card:hover {
          transform: translateY(-8px);
          box-shadow: 0 15px 30px rgba(0, 0, 0, 0.15) !important;
        }
        
        .movie-poster-container {
          overflow: hidden;
        }
        
        .movie-poster {
          transition: transform 0.5s ease;
        }
        
        .hover-card:hover .movie-poster {
          transform: scale(1.05);
        }
        
        .rating-badge {
          background-color: rgba(13, 110, 253, 0.9);
          color: white;
          padding: 8px 12px;
          margin: 12px;
          border-radius: 20px;
          font-weight: bold;
          box-shadow: 0 2px 8px rgba(0, 0, 0, 0.2);
        }
        
        .review-item {
          transition: background-color 0.3s ease;
        }
        
        .review-item:hover {
          background-color: rgba(0, 0, 0, 0.02);
        }
        
        .review-list {
          border-radius: 8px;
        }
        
        @media (max-width: 768px) {
          .stats-col + .stats-col {
            border-left: none;
            border-top: 1px solid rgba(0, 0, 0, 0.08);
          }
        }
      `}</style>
    </div>
  );
}
