import { Container } from "react-bootstrap";
import { useState, useEffect } from "react";
import { Row, Col, Card, Spinner, Badge } from "react-bootstrap";
import { Link } from "react-router-dom";
import axios from "axios";

const Home = () => {
  const [movies, setMovies] = useState([]);
  const [upcomingMovie, setUpcomingMovie] = useState([]);
  const [loading, setLoading] = useState(false);
  const [wathcListIds, setWatchlistIds] = useState([]);
  const [userId, setUserId] = useState(null);

  const sendWatchlist = async (movieId, isAddWishlist) => {
    try {
      const token = localStorage.getItem("tokenMovie");
      const userId = JSON.parse(localStorage.getItem("user")).id;
      console.log("userId", userId);
      console.log("token", token);

      const optionsPost = {
        method: "POST",
        headers: {
          accept: "application/json",
          "content-type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          media_type: "movie",
          media_id: movieId,
          watchlist: !isAddWishlist,
        }),
      };

      fetch(
        `https://api.themoviedb.org/3/account/${userId}/watchlist`,
        optionsPost
      )
        .then((res) => res.json())
        .then((res) => {
          searchMovies();
          console.log(res);
        })
        .catch((err) => {
          searchMovies();
          console.log(err);
        });
    } catch (err) {
      console.error(err);
    }
  };

  const searchMovies = async () => {
    setLoading(true);
    try {
      let token = localStorage.getItem("tokenMovie") || null;
      if (!token) {
        const response = await axios.post("/api/auth/reqToken");
        localStorage.setItem("tokenMovie", response.data.token);
        token = response.data.token;
      }
      const options = {
        method: "GET",
        headers: {
          accept: "application/json",
          Authorization: `Bearer ${token}`,
        },
      };
      const userIdVal = JSON.parse(localStorage.getItem("user"))
        ? JSON.parse(localStorage.getItem("user")).id
        : null;
      setUserId(userIdVal);
      if (userIdVal) {
        fetch(
          `https://api.themoviedb.org/3/account/${userIdVal}/watchlist/movies?language=en-US&page=1&sort_by=created_at.asc`,
          options
        )
          .then((res) => res.json())
          .then((res) => {
            if (res.results) {
              // get the movie ids from res.results and store in an array
              console.log(res);
              const watchlistMapId = res.results.map((movie) => movie.id);
              setWatchlistIds(watchlistMapId);
              searchTopRatedMovies(options);
              searchPopularMovies(options);
            }
          })
          .catch((err) => console.error(err));
      } else {
        searchTopRatedMovies(options);
        searchPopularMovies(options);
      }
    } catch (err) {
      console.error("Search error:", err);
      setUpcomingMovie([]);
    } finally {
      setLoading(false);
    }
  };

  const searchPopularMovies = async (options) => {
    try {
      fetch(
        `https://api.themoviedb.org/3/movie/popular?language=en-US&page=1`,
        options
      )
        .then((res) => res.json())
        .then((res) => {
          if (res.results.length > 8) {
            res.results = res.results.slice(0, 8); // Limit to 8 movies
          }
          getMovieGenre(options, res, 0);
        })
        .catch((err) => console.error(err));
    } catch (err) {
      console.error("Search error:", err);
      setMovies([]);
    }
  };

  const searchTopRatedMovies = async (options) => {
    try {
      fetch(
        `https://api.themoviedb.org/3/movie/top_rated?language=en-US&page=1`,
        options
      )
        .then((res) => res.json())
        .then((res) => {
          if (res.results.length > 8) {
            res.results = res.results.slice(0, 8); // Limit to 8 movies
          }
          getMovieGenre(options, res, 1);
        })
        .catch((err) => console.error(err));
    } catch (err) {
      console.error("Search error:", err);
      setUpcomingMovie([]);
    }
  };

  const getMovieGenre = async (options, moviesList, movieType) => {
    try {
      fetch(
        "https://api.themoviedb.org/3/genre/movie/list?language=en",
        options
      )
        .then((res) => res.json())
        .then((resGenre) => {
          // map res.results to check genre
          moviesList.results.map((movie) => {
            let genresMovie = [];
            movie.genre_ids.map((genreId) => {
              resGenre.genres.map((genreItem) => {
                if (genreItem.id === genreId) {
                  genresMovie.push(genreItem.name);
                }
              });
            });
            movie.genres = genresMovie;
          });
          if (movieType === 1) {
            setUpcomingMovie(moviesList.results || []);
          } else {
            setMovies(moviesList.results || []);
          }
        })
        .catch((err) => console.error(err));
    } catch (err) {
      console.error(err);
    }
  };
  
  useEffect(() => {
    const timer = setTimeout(searchMovies, 500);
    return () => clearTimeout(timer);
  }, []);

  // Get color based on rating
  const getRatingColor = (rating) => {
    if (rating >= 4) return "success";
    if (rating >= 3) return "info";
    if (rating >= 2) return "warning";
    return "danger";
  };

  return (
    <div className="bg-dark bg-gradient text-white min-vh-100">
      {/* Hero Section */}
      <div className="position-relative overflow-hidden p-3 p-md-5 text-center bg-dark" 
           style={{
             backgroundImage: "linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.7)), url('https://image.tmdb.org/t/p/original/wwemzKWzjKYJFfCeiB57q3r4Bcm.png')",
             backgroundSize: "cover",
             backgroundPosition: "center",
             height: "50vh",
             minHeight: "400px"
           }}>
        <div className="col-md-8 mx-auto my-5">
          <h1 className="display-3 fw-bold text-white mb-3">Welcome to Cinerate</h1>
          <p className="lead fw-normal text-white-50 mb-4">Discover, rate, and review your favorite movies from our curated collection</p>
          <div className="d-flex gap-3 justify-content-center">
            <a href="#popular" className="btn btn-primary btn-lg px-4 fw-bold">Popular Movies</a>
            <a href="#toprated" className="btn btn-outline-light btn-lg px-4">Top Rated</a>
          </div>
        </div>
      </div>
      
      {loading && (
        <div className="d-flex justify-content-center align-items-center" style={{height: "300px"}}>
          <div className="text-center">
            <Spinner animation="border" variant="primary" style={{ width: '4rem', height: '4rem' }} />
            <p className="mt-3 text-light">Loading amazing movies for you...</p>
          </div>
        </div>
      )}

      {!loading && movies.length === 0 && (
        <div className="text-center py-5 my-5">
          <svg xmlns="http://www.w3.org/2000/svg" width="64" height="64" fill="currentColor" className="bi bi-film mb-3 text-secondary" viewBox="0 0 16 16">
            <path d="M0 1a1 1 0 0 1 1-1h14a1 1 0 0 1 1 1v14a1 1 0 0 1-1 1H1a1 1 0 0 1-1-1V1zm4 0v6h8V1H4zm8 8H4v6h8V9zM1 1v2h2V1H1zm2 3H1v2h2V4zM1 7v2h2V7H1zm2 3H1v2h2v-2zm-2 3v2h2v-2H1zM15 1h-2v2h2V1zm-2 3v2h2V4h-2zm2 3h-2v2h2V7zm-2 3v2h2v-2h-2zm2 3h-2v2h2v-2z"/>
          </svg>
          <h3 className="text-secondary">No movies found.</h3>
          <p className="text-muted">Please try again later or check your connection.</p>
        </div>
      )}

      {/* Popular Movies Section */}
      <Container id="popular" className="py-5">
        <div className="d-flex align-items-center mb-4">
          <div className="bg-primary" style={{width: "5px", height: "30px", marginRight: "15px"}}></div>
          <h2 className="display-6 fw-bold m-0">Popular Movies</h2>
          <div className="ms-auto">
            <Link to="/popular" className="btn btn-sm btn-outline-light">View All</Link>
          </div>
        </div>
        <p className="lead text-white-50 mb-4">The most watched films right now</p>

        <Row xs={1} sm={2} md={2} lg={4} className="g-4">
          {movies?.map((movie) => (
            <Col key={movie.id}>
              <Card className="bg-dark text-white h-100 border-0 rounded-4 overflow-hidden movie-card">
                <div className="position-relative overflow-hidden movie-poster">
                  <Card.Img
                    variant="top"
                    src={
                      movie.poster_path
                        ? `https://image.tmdb.org/t/p/w500${movie.poster_path}`
                        : "/placeholder.jpg"
                    }
                    style={{ height: "380px", objectFit: "cover" }}
                    className="img-fluid transition-transform"
                  />
                  <div className="movie-overlay d-flex align-items-end p-3">
                    <div className="w-100">
                      <div className="d-flex justify-content-between align-items-center mb-2">
                        <Badge bg={getRatingColor(movie.vote_average/2)} className="px-2 py-1 rounded-pill">
                          ★ {movie.vote_average > 5 ? 5 : movie.vote_average.toFixed(1)}
                        </Badge>
                        <small className="text-light opacity-75">
                          {movie.release_date && new Date(movie.release_date).getFullYear()}
                        </small>
                      </div>
                      {userId && (
                        <button
                          onClick={(e) => {
                            e.preventDefault();
                            sendWatchlist(movie.id, wathcListIds.includes(movie.id));
                          }}
                          className={
                            wathcListIds.includes(movie.id)
                              ? "btn btn-sm btn-warning w-100 mb-2"
                              : "btn btn-sm btn-outline-light w-100 mb-2"
                          }
                        >
                          {wathcListIds.includes(movie.id)
                            ? "Remove From Watchlist"
                            : "Add To Watchlist"}
                        </button>
                      )}
                      <Link
                        to={`/movie/${movie.id}`}
                        className="btn btn-primary btn-sm w-100"
                      >
                        View Details
                      </Link>
                    </div>
                  </div>
                </div>
                <Card.Body className="p-3">
                  <Card.Title className="fw-bold text-truncate mb-1">{movie.title}</Card.Title>
                  <div className="d-flex flex-wrap gap-1 mt-2">
                    {movie.genres && movie.genres.slice(0, 3).map((genre, idx) => (
                      <Badge key={idx} bg="secondary" className="rounded-pill font-monospace" style={{fontSize: "0.7rem"}}>
                        {genre}
                      </Badge>
                    ))}
                  </div>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>

      {/* Top Rated Movies Section */}
      <div className="py-4 bg-dark bg-opacity-50">
        <Container id="toprated" className="py-5">
          <div className="d-flex align-items-center mb-4">
            <div className="bg-success" style={{width: "5px", height: "30px", marginRight: "15px"}}></div>
            <h2 className="display-6 fw-bold m-0">Top Rated Movies</h2>
            <div className="ms-auto">
              <Link to="/top-rated" className="btn btn-sm btn-outline-light">View All</Link>
            </div>
          </div>
          <p className="lead text-white-50 mb-4">Critically acclaimed masterpieces</p>

          <Row xs={1} sm={2} md={2} lg={4} className="g-4">
            {upcomingMovie?.map((movie) => (
              <Col key={movie.id}>
                <Card className="bg-dark text-white h-100 border-0 rounded-4 overflow-hidden movie-card">
                  <div className="position-relative overflow-hidden movie-poster">
                    <Card.Img
                      variant="top"
                      src={
                        movie.poster_path
                          ? `https://image.tmdb.org/t/p/w500${movie.poster_path}`
                          : "/placeholder.jpg"
                      }
                      style={{ height: "380px", objectFit: "cover" }}
                      className="img-fluid transition-transform"
                    />
                    <div className="movie-overlay d-flex align-items-end p-3">
                      <div className="w-100">
                        <div className="d-flex justify-content-between align-items-center mb-2">
                          <Badge bg={getRatingColor(movie.vote_average/2)} className="px-2 py-1 rounded-pill">
                            ★ {movie.vote_average > 5 ? 5 : movie.vote_average.toFixed(1)}
                          </Badge>
                          <small className="text-light opacity-75">
                            {movie.release_date && new Date(movie.release_date).getFullYear()}
                          </small>
                        </div>
                        {userId && (
                          <button
                            onClick={(e) => {
                              e.preventDefault();
                              sendWatchlist(movie.id, wathcListIds.includes(movie.id));
                            }}
                            className={
                              wathcListIds.includes(movie.id)
                                ? "btn btn-sm btn-warning w-100 mb-2"
                                : "btn btn-sm btn-outline-light w-100 mb-2"
                            }
                          >
                            {wathcListIds.includes(movie.id)
                              ? "Remove From Watchlist"
                              : "Add To Watchlist"}
                          </button>
                        )}
                        <Link
                          to={`/movie/${movie.id}`}
                          className="btn btn-success btn-sm w-100"
                        >
                          View Details
                        </Link>
                      </div>
                    </div>
                  </div>
                  <Card.Body className="p-3">
                    <Card.Title className="fw-bold text-truncate mb-1">{movie.title}</Card.Title>
                    <div className="d-flex flex-wrap gap-1 mt-2">
                      {movie.genres && movie.genres.slice(0, 3).map((genre, idx) => (
                        <Badge key={idx} bg="secondary" className="rounded-pill font-monospace" style={{fontSize: "0.7rem"}}>
                          {genre}
                        </Badge>
                      ))}
                    </div>
                  </Card.Body>
                </Card>
              </Col>
            ))}
          </Row>
        </Container>
      </div>

      {/* Add custom CSS for hover effects */}
      <style jsx="true">{`
        .movie-card {
          transition: transform 0.3s ease, box-shadow 0.3s ease;
        }
        .movie-card:hover {
          transform: translateY(-10px);
          box-shadow: 0 20px 30px rgba(0, 0, 0, 0.5);
        }
        .movie-poster {
          position: relative;
        }
        .movie-poster img {
          transition: transform 0.5s ease;
        }
        .movie-card:hover .movie-poster img {
          transform: scale(1.05);
        }
        .movie-overlay {
          position: absolute;
          bottom: 0;
          left: 0;
          right: 0;
          background: linear-gradient(transparent, rgba(0,0,0,0.9));
          opacity: 0;
          transition: opacity 0.3s ease;
        }
        .movie-card:hover .movie-overlay {
          opacity: 1;
        }
      `}</style>
    </div>
  );
};

export default Home;
