import { Container } from "react-bootstrap";
import { useState, useEffect } from "react";
import { Row, Col, Card, Spinner } from "react-bootstrap";
import { Link } from "react-router-dom";
import axios from "axios";
const Home = () => {
  const [movies, setMovies] = useState([]);
  const [upcomingMovie, setUpcomingMovie] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const searchMovies = async () => {
      setLoading(true);
      try {
        let token = localStorage.getItem("tokenMovie") || null;
        if (!token) {
          const response = await axios.post("/api/auth/reqToken");
          console.log("Token response:", response.data);
          localStorage.setItem("tokenMovie", response.data.token);
          token = response.data.token;
        }
        const options = {
          method: "GET",
          headers: {
            accept: "application/json",
            Authorization: `Bearer ${token}`,
          },
        };
        fetch(
          `https://api.themoviedb.org/3/movie/popular?language=en-US&page=1`,
          options
        )
          .then((res) => res.json())
          .then((res) => {
            if (res.results.length > 8) {
              res.results = res.results.slice(0, 8); // Limit to 8 movies
            }
            setMovies(res.results || []);
          })
          .catch((err) => console.error(err));

        fetch(
          `https://api.themoviedb.org/3/movie/top_rated?language=en-US&page=1`,
          options
        )
          .then((res) => res.json())
          .then((res) => {
            if (res.results.length > 8) {
              res.results = res.results.slice(0, 8); // Limit to 8 movies
            }
            setUpcomingMovie(res.results || []);
          })
          .catch((err) => console.error(err));
      } catch (err) {
        console.error("Search error:", err);
        setUpcomingMovie([]);
      } finally {
        setLoading(false);
      }
    };

    const timer = setTimeout(searchMovies, 500);
    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="p-4">
      mport { useState, useEffect } from "react";
import { Form, Row, Col, Card, Spinner } from "react-bootstrap";
import { Link } from "react-router-dom";
import axios from "axios";
      {loading && <Spinner animation="border" className="d-block mx-auto" />}

      {!loading && movies.length === 0 && (
        <div className="text-center text-muted mb-4">No movies found.</div>
      )}
      <Container className="text-center mt-5">
        <h2>Popular Movies</h2>
        <p className="lead">Top 8 Popular Movies</p>
      </Container>
      <Row xs={1} md={4} className="g-4">
        {movies?.map((movie) => (
          <Col key={movie.id}>
            <Card className="h-100 shadow-sm">
              <Card.Img
                variant="top"
                src={
                  movie.poster_path
                    ? `https://image.tmdb.org/t/p/w500${movie.poster_path}`
                    : "/placeholder.jpg"
                }
                style={{ height: "400px", objectFit: "cover" }}
              />
              <Card.Body className="d-flex flex-column">
                <Card.Title>{movie.title}</Card.Title>
                <Card.Text className="text-muted small mt-auto">
                  {movie.release_date &&
                    new Date(movie.release_date).getFullYear()}
                  {movie.vote_average &&
                    ` • ⭐ ${
                      movie.vote_average > 5 ? 5 : movie.vote_average.toFixed(1)
                    }`}
                </Card.Text>
                <Link
                  to={`/movie/${movie.id}`}
                  className="btn btn-primary w-100 mt-2"
                >
                  View Details
                </Link>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>

      <Container className="text-center mt-5">
        <h2>Top Rated Movies</h2>
        <p className="lead">Top Picks for You</p>
      </Container>
      <Row xs={1} md={4} className="g-4">
        {upcomingMovie?.map((movie) => (
          <Col key={movie.id}>
            <Card className="h-100 shadow-sm">
              <Card.Img
                variant="top"
                src={
                  movie.poster_path
                    ? `https://image.tmdb.org/t/p/w500${movie.poster_path}`
                    : "/placeholder.jpg"
                }
                style={{ height: "400px", objectFit: "cover" }}
              />
              <Card.Body className="d-flex flex-column">
                <Card.Title>{movie.title}</Card.Title>
                <Card.Text className="text-muted small mt-auto">
                  {movie.release_date &&
                    new Date(movie.release_date).getFullYear()}
                  {movie.vote_average &&
                    ` • ⭐ ${
                      movie.vote_average > 5 ? 5 : movie.vote_average.toFixed(1)
                    }`}
                </Card.Text>
                <Link
                  to={`/movie/${movie.id}`}
                  className="btn btn-primary w-100 mt-2"
                >
                  View Details
                </Link>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </div>
  );
};

export default Home;
