import React, { useEffect, useState } from "react";
import axios from "axios";
import { Card, Row, Col } from "react-bootstrap";

const Filters = () => {
  const [mostViewed, setMostViewed] = useState([]);
  const [highestRated, setHighestRated] = useState([]);

  useEffect(() => {
    const fetchMostViewed = async () => {
      try {
        const res = await axios.get("/api/movies/most-viewed");
        setMostViewed(res.data);
      } catch (err) {
        console.error(err);
      }
    };

    const fetchHighestRated = async () => {
      try {
        const res = await axios.get("/api/movies/highest-rated");
        setHighestRated(res.data);
      } catch (err) {
        console.error(err);
      }
    };

    fetchMostViewed();
    fetchHighestRated();
  }, []);

  return (
    <>
      <h3>Most Viewed Movies</h3>
      <Row xs={1} md={4} className="g-4">
        {mostViewed.map((movie) => (
          <Col key={movie._id}>
            <Card>
              <Card.Img
                variant="top"
                src={`https://image.tmdb.org/t/p/w500${movie.posterPath}`}
              />
              <Card.Body>
                <Card.Title>{movie.title}</Card.Title>
                <Card.Text>{movie.overview.substring(0, 100)}...</Card.Text>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>

      <h3>Highest Rated Movies</h3>
      <Row xs={1} md={4} className="g-4">
        {highestRated.map((movie) => (
          <Col key={movie._id}>
            <Card>
              <Card.Img
                variant="top"
                src={`https://image.tmdb.org/t/p/w500${movie.posterPath}`}
              />
              <Card.Body>
                <Card.Title>{movie.title}</Card.Title>
                <Card.Text>{movie.overview.substring(0, 100)}...</Card.Text>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </>
  );
};

export default Filters;
