import React, { useEffect, useState } from "react";
import { ListGroup, Card, Spinner, Row, Col } from "react-bootstrap";
import axios from "axios";
import { Link } from "react-router-dom";

function Profile() {
  const [profileData, setProfileData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await axios.get("/api/users/me");
        setProfileData(response.data);
      } catch (err) {
        console.error("Profile load error:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchProfile();
  }, []);

  if (loading) {
    return <Spinner animation="border" className="d-block mx-auto mt-5" />;
  }

  return (
    <div className="container mt-4">
      {}
      <Card className="mb-4">
        <Card.Body>
          <Card.Title>{profileData.user.username}</Card.Title>
          <Card.Text>
            Member since:{" "}
            {new Date(profileData.user.createdAt).toLocaleDateString()}
            <br />
            Followers: {profileData.user.followers}
          </Card.Text>
        </Card.Body>
      </Card>

      {/* Reviews Section */}
      <h3>My Reviews</h3>
      {profileData.reviews.length === 0 ? (
        <p className="text-muted">No reviews yet</p>
      ) : (
        <ListGroup className="mb-4">
          {profileData.reviews.map((review) => (
            <ListGroup.Item key={review._id}>
              <div className="d-flex justify-content-between align-items-start">
                <div>
                  <h5>
                    <Link to={`/movie/${review.movie.tmdbId}`}>
                      {review.movie.title}
                    </Link>
                  </h5>
                  <div className="text-warning">
                    {"★".repeat(review.rating)}
                    {"☆".repeat(5 - review.rating)}
                  </div>
                  <p>{review.comment}</p>
                </div>
                <small className="text-muted">
                  {new Date(review.createdAt).toLocaleDateString()}
                </small>
              </div>
            </ListGroup.Item>
          ))}
        </ListGroup>
      )}

      {/* Watchlist Section */}
      <h3>My Watchlist</h3>
      {profileData.watchlist.length === 0 ? (
        <p className="text-muted">Watchlist is empty</p>
      ) : (
        <Row xs={1} md={4} className="g-4">
          {profileData.watchlist.map((movie) => (
            <Col key={movie._id}>
              <Card className="h-100">
                <Card.Img
                  variant="top"
                  src={`https://image.tmdb.org/t/p/w500${movie.posterPath}`}
                />
                <Card.Body>
                  <Card.Title>{movie.title}</Card.Title>
                  <Card.Text className="text-muted small">
                    {new Date(movie.releaseDate).getFullYear()}
                  </Card.Text>
                  <Link
                    to={`/movie/${movie._id}`}
                    className="btn btn-primary w-100"
                  >
                    View Details
                  </Link>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      )}
    </div>
  );
}

export default Profile;
