import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { Container, Card, Form, Button, Row, Col, Badge } from "react-bootstrap";
import axios from "axios";

export default function Movie() {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [rating, setRating] = useState(5);
  const [comment, setComment] = useState("");
  const [userId, setUserId] = useState([]);
  const [wathcListIds, setWatchlistIds] = useState([]);

  const sendWatchlist = async (movieId, isAddWishlist) => {
    try {
      const token = localStorage.getItem("tokenMovie");
      const userId = JSON.parse(localStorage.getItem("user")).id;
      console.log("userId", userId);
      console.log("token", token);

      const optionsPost = {
        method: "POST",
        headers: {
          accept: "application/json",
          "content-type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          media_type: "movie",
          media_id: movieId,
          watchlist: !isAddWishlist,
        }),
      };

      fetch(
        `https://api.themoviedb.org/3/account/${userId}/watchlist`,
        optionsPost
      )
        .then((res) => res.json())
        .then((res) => {
          fetchData();
          console.log(res);
        })
        .catch((err) => {
          fetchData();
          console.log(err);
        });
    } catch (err) {
      console.error(err);
    }
  };

  const fetchData = async () => {
    try {
      let token = localStorage.getItem("tokenMovie") || null;
      if (!token) {
        const response = await axios.post("/api/auth/reqToken");
        console.log("Token response:", response.data);
        localStorage.setItem("tokenMovie", response.data.token);
        token = response.data.token;
      }

      const options = {
        method: "GET",
        headers: {
          accept: "application/json",
          Authorization: `Bearer ${token}`,
        },
      };

      const userIdVal = JSON.parse(localStorage.getItem("user"))
        ? JSON.parse(localStorage.getItem("user")).id
        : null;
      setUserId(userIdVal);
      if (userIdVal) {
        fetch(
          `https://api.themoviedb.org/3/account/${userIdVal}/watchlist/movies?language=en-US&page=1&sort_by=created_at.asc`,
          options
        )
          .then((res) => res.json())
          .then((res) => {
            if (res.results) {
              // get the movie ids from res.results and store in an array
              console.log(res);
              const watchlistMapId = res.results.map((movie) => movie.id);
              setWatchlistIds(watchlistMapId);
              fetchMovie(options);
              fetchReviews(options);
            }
          })
          .catch((err) => console.error(err));
      } else {
        fetchMovie(options);
        fetchReviews(options);
      }
    } catch (err) {
      console.error(err);
    }
  };

