import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { Container, Card, Form, Button, Row, Col } from "react-bootstrap";
import axios from "axios";

export default function Movie() {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [rating, setRating] = useState(5);
  const [comment, setComment] = useState("");
  const [userId, setUserId] = useState([]);
  const [wathcListIds, setWatchlistIds] = useState([]);

  const sendWatchlist = async (movieId, isAddWishlist) => {
    try {
      const token = localStorage.getItem("tokenMovie");
      const userId = JSON.parse(localStorage.getItem("user")).id;
      console.log("userId", userId);
      console.log("token", token);

      const optionsPost = {
        method: "POST",
        headers: {
          accept: "application/json",
          "content-type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          media_type: "movie",
          media_id: movieId,
          watchlist: !isAddWishlist,
        }),
      };

      fetch(
        `https://api.themoviedb.org/3/account/${userId}/watchlist`,
        optionsPost
      )
        .then((res) => res.json())
        .then((res) => {
          fetchData();
          console.log(res);
        })
        .catch((err) => {
          fetchData();
          console.log(err);
        });
    } catch (err) {
      console.error(err);
    }
  };

  const fetchData = async () => {
    try {
      let token = localStorage.getItem("tokenMovie") || null;
      if (!token) {
        const response = await axios.post("/api/auth/reqToken");
        console.log("Token response:", response.data);
        localStorage.setItem("tokenMovie", response.data.token);
        token = response.data.token;
      }

      const options = {
        method: "GET",
        headers: {
          accept: "application/json",
          Authorization: `Bearer ${token}`,
        },
      };

      const userIdVal = JSON.parse(localStorage.getItem("user"))
        ? JSON.parse(localStorage.getItem("user")).id
        : null;
      setUserId(userIdVal);
      if (userIdVal) {
        fetch(
          `https://api.themoviedb.org/3/account/${userIdVal}/watchlist/movies?language=en-US&page=1&sort_by=created_at.asc`,
          options
        )
          .then((res) => res.json())
          .then((res) => {
            if (res.results) {
              // get the movie ids from res.results and store in an array
              console.log(res);
              const watchlistMapId = res.results.map((movie) => movie.id);
              setWatchlistIds(watchlistMapId);
              fetchMovie(options);
              fetchReviews(options);
            }
          })
          .catch((err) => console.error(err));
      } else {
        fetchMovie(options);
        fetchReviews(options);
      }
    } catch (err) {
      console.error(err);
    }
  };

  // const getMovieGenre = async (options, moviesList) => {
  //   try {
  //     fetch(
  //       "https://api.themoviedb.org/3/genre/movie/list?language=en",
  //       options
  //     )
  //       .then((res) => res.json())
  //       .then((resGenre) => {
  //         // map res.results to check genre
  //         moviesList.results.map((movie) => {
  //           let genresMovie = [];
  //           movie.genre_ids.map((genreId) => {
  //             resGenre.genres.map((genreItem) => {
  //               if (genreItem.id === genreId) {
  //                 genresMovie.push(genreItem.name);
  //               }
  //             });
  //           });
  //           movie.genres = genresMovie;
  //         });
  //         setMovie(moviesList.results || []);
  //       })
  //       .catch((err) => console.error(err));
  //   } catch (err) {
  //     console.error(err);
  //   }
  // };

  const fetchMovie = async (options) => {
    await fetch(
      `https://api.themoviedb.org/3/movie/${id}?language=en-US`,
      options
    )
      .then((res) => res.json())
      .then((res) => {
        console.log(res);
        setMovie(res || []);
      })
      .catch((err) => console.error(err));
  };

  const fetchReviews = async (options) => {
    await fetch(
      `https://api.themoviedb.org/3/movie/${id}/reviews?language=en-US`,
      options
    )
      .then((res) => res.json())
      .then((res) => {
        console.log(res);
        setReviews(res.results || []);
      })
      .catch((err) => console.error(err));
  };

  useEffect(() => {
    fetchData();
  }, [id]);
  // check submit
  const submitReview = async () => {
    try {
      if (!userId) {
        alert("Please login to submit a review");
        return;
      }
      const token = localStorage.getItem("token");
      console.log(rating);
      console.log(comment);
      await axios.post(
        "/api/reviews",
        {
          movie: id,
          rating,
          comment,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      alert("Review submitted successfully!");
    } catch (err) {
      alert("Error submitting review");
    }
  };

  return (
    <Container className="py-4">
      {movie && (
        <>
          <Row className="mb-4">
            <Col md={4}>
              <div className="mb-3">
                <img
                  src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`}
                  alt={movie.title}
                  className="img-fluid rounded shadow"
                />
              </div>
              <div className="mb-3">
                {userId && (
                  <button
                    onClick={() =>
                      sendWatchlist(movie.id, wathcListIds.includes(movie.id))
                    }
                    className={
                      wathcListIds.includes(movie.id)
                        ? "btn btn-warning w-100 mt-2"
                        : "btn btn-primary w-100 mt-2"
                    }
                  >
                    {wathcListIds.includes(movie.id)
                      ? "Remove From Watchlist"
                      : "Add To Watchlist"}
                  </button>
                )}
              </div>
            </Col>
            <Col md={8}>
              <h1>{movie.title}</h1>
              <p className="lead">{movie.overview}</p>
              <div className="mb-3">
                <span className="fw-bold">Release Date:</span>{" "}
                {movie.release_date}
                {movie.genres && (
                  <div>
                    <span className="fw-bold">Genres:</span>{" "}
                    {movie.genres.map((genre) => genre.name).join(", ")}
                  </div>
                )}
              </div>

              <div className="mb-3">
                <Card className="mb-4">
                  <Card.Body>
                    <Card.Title>Write a Review</Card.Title>
                    <Form>
                      <Form.Group className="mb-3">
                        <Form.Label>Rating</Form.Label>
                        <Form.Select
                          value={rating}
                          onChange={(e) => setRating(e.target.value)}
                        >
                          {[5, 4, 3, 2, 1].map((num) => (
                            <option key={num} value={num}>
                              {num} Stars
                            </option>
                          ))}
                        </Form.Select>
                      </Form.Group>
                      <Form.Group className="mb-3">
                        <Form.Control
                          as="textarea"
                          rows={3}
                          placeholder="Your review..."
                          value={comment}
                          onChange={(e) => setComment(e.target.value)}
                        />
                      </Form.Group>
                      <Button variant="primary" onClick={submitReview}>
                        Submit Review
                      </Button>
                    </Form>
                  </Card.Body>
                </Card>
              </div>
            </Col>
          </Row>

          <h3 className="mb-3">User Reviews</h3>
          {reviews.map((review) => (
            <Card key={review.id} className="mb-3">
              <Card.Body>
                <div className="d-flex justify-content-between mb-2">
                  <strong>{review.author}</strong>
                  <div>
                    {Array(review.author_details.rating)
                      .fill()
                      .map((_, i) => (i < 5 ? <span key={i}>⭐</span> : null))}
                  </div>
                </div>
                <p>{review.content}</p>
              </Card.Body>
            </Card>
          ))}
        </>
      )}
    </Container>
  );
}
