import { Container } from "react-bootstrap";
import { useState, useEffect } from "react";
import { Row, Col, Card, Spinner } from "react-bootstrap";
import { Link } from "react-router-dom";
import axios from "axios";

const Home = () => {
  const [movies, setMovies] = useState([]);
  const [upcomingMovie, setUpcomingMovie] = useState([]);
  const [loading, setLoading] = useState(false);
  const [wathcListIds, setWatchlistIds] = useState([]);
  const [userId, setUserId] = useState(null);

  const sendWatchlist = async (movieId, isAddWishlist) => {
    try {
      const token = localStorage.getItem("tokenMovie");
      const userId = JSON.parse(localStorage.getItem("user")).id;
      console.log("userId", userId);
      console.log("token", token);

      const optionsPost = {
        method: "POST",
        headers: {
          accept: "application/json",
          "content-type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          media_type: "movie",
          media_id: movieId,
          watchlist: !isAddWishlist,
        }),
      };

      fetch(
        `https://api.themoviedb.org/3/account/${userId}/watchlist`,
        optionsPost
      )
        .then((res) => res.json())
        .then((res) => {
          searchMovies();
          console.log(res);
        })
        .catch((err) => {
          searchMovies();
          console.log(err);
        });
    } catch (err) {
      console.error(err);
    }
  };

  const searchMovies = async () => {
    setLoading(true);
    try {
      let token = localStorage.getItem("tokenMovie") || null;
      if (!token) {
        const response = await axios.post("/api/auth/reqToken");
        localStorage.setItem("tokenMovie", response.data.token);
        token = response.data.token;
      }
      const options = {
        method: "GET",
        headers: {
          accept: "application/json",
          Authorization: `Bearer ${token}`,
        },
      };
      const userIdVal = JSON.parse(localStorage.getItem("user"))
        ? JSON.parse(localStorage.getItem("user")).id
        : null;
      setUserId(userIdVal);
      if (userIdVal) {
        fetch(
          `https://api.themoviedb.org/3/account/${userIdVal}/watchlist/movies?language=en-US&page=1&sort_by=created_at.asc`,
          options
        )
          .then((res) => res.json())
          .then((res) => {
            if (res.results) {
              // get the movie ids from res.results and store in an array
              console.log(res);
              const watchlistMapId = res.results.map((movie) => movie.id);
              setWatchlistIds(watchlistMapId);
              searchTopRatedMovies(options);
              searchPopularMovies(options);
            }
          })
          .catch((err) => console.error(err));
      } else {
        searchTopRatedMovies(options);
        searchPopularMovies(options);
      }
    } catch (err) {
      console.error("Search error:", err);
      setUpcomingMovie([]);
    } finally {
      setLoading(false);
    }
  };

  const searchPopularMovies = async (options) => {
    try {
      fetch(
        `https://api.themoviedb.org/3/movie/popular?language=en-US&page=1`,
        options
      )
        .then((res) => res.json())
        .then((res) => {
          if (res.results.length > 8) {
            res.results = res.results.slice(0, 8); // Limit to 8 movies
          }
          getMovieGenre(options, res, 0);
        })
        .catch((err) => console.error(err));
    } catch (err) {
      console.error("Search error:", err);
      setMovies([]);
    }
  };

  const searchTopRatedMovies = async (options) => {
    try {
      fetch(
        `https://api.themoviedb.org/3/movie/top_rated?language=en-US&page=1`,
        options
      )
        .then((res) => res.json())
        .then((res) => {
          if (res.results.length > 8) {
            res.results = res.results.slice(0, 8); // Limit to 8 movies
          }
          getMovieGenre(options, res, 1);
        })
        .catch((err) => console.error(err));
    } catch (err) {
      console.error("Search error:", err);
      setUpcomingMovie([]);
    }
  };

  const getMovieGenre = async (options, moviesList, movieType) => {
    try {
      fetch(
        "https://api.themoviedb.org/3/genre/movie/list?language=en",
        options
      )
        .then((res) => res.json())
        .then((resGenre) => {
          // map res.results to check genre
          moviesList.results.map((movie) => {
            let genresMovie = [];
            movie.genre_ids.map((genreId) => {
              resGenre.genres.map((genreItem) => {
                if (genreItem.id === genreId) {
                  genresMovie.push(genreItem.name);
                }
              });
            });
            movie.genres = genresMovie;
          });
          if (movieType === 1) {
            setUpcomingMovie(moviesList.results || []);
          } else {
            setMovies(moviesList.results || []);
          }
        })
        .catch((err) => console.error(err));
    } catch (err) {
      console.error(err);
    }
  };
  useEffect(() => {
    const timer = setTimeout(searchMovies, 500);
    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="bg-light min-vh-100 py-5">
      <Container className="text-center mb-5">
        <h1 className="display-4 fw-bold text-primary mb-2">Welcome to Cinerate</h1>
        <p className="lead text-secondary">Discover, rate, and review your favorite movies</p>
      </Container>
      
      {loading && (
        <div className="d-flex justify-content-center my-5">
          <Spinner animation="border" variant="primary" style={{ width: '3rem', height: '3rem' }} />
        </div>
      )}

      {!loading && movies.length === 0 && (
        <div className="text-center text-muted my-5 py-5">
          <i className="bi bi-film me-2"></i>
          No movies found.
        </div>
      )}

      <Container className="mb-4">
        <div className="border-bottom border-2 border-primary mb-4 pb-2">
          <h2 className="fw-bold">Popular Movies</h2>
          <p className="lead text-muted mb-0">Top 8 Popular Movies</p>
        </div>

        <Row xs={1} sm={2} md={3} lg={4} className="g-4">
          {movies?.map((movie) => (
            <Col key={movie.id}>
              <Card className="h-100 shadow border-0 rounded-3 overflow-hidden transition-transform hover-lift">
                <div className="position-relative">
                  <Card.Img
                    variant="top"
                    src={
                      movie.poster_path
                        ? `https://image.tmdb.org/t/p/w500${movie.poster_path}`
                        : "/placeholder.jpg"
                    }
                    style={{ height: "300px", objectFit: "cover" }}
                    className="img-fluid"
                  />
                  <div className="position-absolute top-0 end-0 bg-dark bg-opacity-75 text-white px-2 py-1 m-2 rounded-pill">
