      </Container>

      <Container className="mt-5 mb-4">
        <div className="border-bottom border-2 border-success mb-4 pb-2">
          <h2 className="fw-bold">Top Rated Movies</h2>
          <p className="lead text-muted mb-0">Top Picks for You</p>
        </div>

        <Row xs={1} sm={2} md={3} lg={4} className="g-4">
          {upcomingMovie?.map((movie) => (
            <Col key={movie.id}>
              <Card className="h-100 shadow border-0 rounded-3 overflow-hidden transition-transform hover-lift">
                <div className="position-relative">
                  <Card.Img
                    variant="top"
                    src={
                      movie.poster_path
                        ? `https://image.tmdb.org/t/p/w500${movie.poster_path}`
