                    {movie.vote_average && `⭐ ${movie.vote_average > 5 ? 5 : movie.vote_average.toFixed(1)}`}
                  </div>
                </div>
                <Card.Body className="d-flex flex-column">
                  <Card.Title className="fw-bold text-truncate">{movie.title}</Card.Title>
                  <div className="mb-2">
                    <small className="text-muted">
                      {movie.release_date && new Date(movie.release_date).getFullYear()}
                    </small>
                  </div>
                  <Card.Text className="mb-3">
                    <small className="text-secondary">
                      {movie.genres && movie.genres.join(", ")}
                    </small>
                  </Card.Text>
                  <div className="mt-auto">
                    <Link
                      to={`/movie/${movie.id}`}
                      className="btn btn-outline-success w-100 mb-2"
                    >
                      View Details
                    </Link>
                    {userId && (
                      <button
                        onClick={() =>
                          sendWatchlist(movie.id, wathcListIds.includes(movie.id))
                        }
                        className={
                          wathcListIds.includes(movie.id)
                            ? "btn btn-warning w-100"
                            : "btn btn-outline-dark w-100"
                        }
                      >
                        {wathcListIds.includes(movie.id)
                          ? "Remove From Watchlist"
                          : "Add To Watchlist"}
                      </button>
                    )}
                  </div>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>
    </div>
  );
};

export default Home;
