import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { Container, Card, Form, Button, Row, Col } from "react-bootstrap";
import axios from "axios";

export default function Movie() {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [rating, setRating] = useState(5);
  const [comment, setComment] = useState("");
  const [inWatchlist, setInWatchlist] = useState(false);


  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch movie details
        const movieResponse = await axios.get(
          `https://api.themoviedb.org/3/movie/${id}?api_key=${process.env.REACT_APP_TMDB_API_KEY}`
        );
        
        // Fetch reviews
        const reviewsResponse = await axios.get(
          `https://api.themoviedb.org/3/movie/${id}/reviews?api_key=${process.env.REACT_APP_TMDB_API_KEY}`
        );

        // Check watchlist status
        const watchlistCheck = await axios.get(`/api/users/watchlist/check/${id}`, {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
        });

        setMovie(movieResponse.data);
        setReviews(reviewsResponse.data.results || []);
        setInWatchlist(watchlistCheck.data.inWatchlist);
      } catch (err) {
        console.error(err);
      }
    };
    fetchData();
  }, [id]);

  const handleWatchlist = async () => {
    try {
      const endpoint = inWatchlist ? "remove" : "add";
      await axios.post(`/api/users/watchlist/${endpoint}/${id}`, null, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
      });
      setInWatchlist(!inWatchlist);
    } catch (err) {
      alert("Error updating watchlist");
    }
  };


  const submitReview = async () => {
    try {
      const token = localStorage.getItem("token");
      await axios.post(
        "/api/reviews",
        {
          movie: id,
          rating,
          comment,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      alert("Review submitted successfully!");
    } catch (err) {
      alert("Error submitting review");
    }
  };

  return (
    <Container className="py-4">
      {movie && (
        <>
          <Row className="mb-4">
            <Col md={4}>
              <img
                src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`}
                alt={movie.title}
                className="img-fluid rounded shadow"
              />
            </Col>
            <Col md={8}>
              <h1>{movie.title}</h1>
              <p className="lead">{movie.overview}</p>
              <div className="mb-3">
                <span className="fw-bold">Release Date:</span>{" "}
                {movie.release_date}
              </div>
            </Col>
          </Row>

          <Card className="mb-4">
            <Card.Body>
              <Card.Title>Write a Review</Card.Title>
              <Form>
                <Form.Group className="mb-3">
                  <Form.Label>Rating</Form.Label>
                  <Form.Select
                    value={rating}
                    onChange={(e) => setRating(e.target.value)}
                  >
                    {[5, 4, 3, 2, 1].map((num) => (
                      <option key={num} value={num}>
                        {num} Stars
                      </option>
                    ))}
                  </Form.Select>
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Control
                    as="textarea"
                    rows={3}
                    placeholder="Your review..."
                    value={comment}
                    onChange={(e) => setComment(e.target.value)}
                  />
                </Form.Group>
                <Button variant="primary" onClick={submitReview}>
                  Submit Review
                </Button>
              </Form>
            </Card.Body>
          </Card>

          <h3 className="mb-3">User Reviews</h3>
          {reviews.map((review) => (
            <Card key={review.id} className="mb-3">
              <Card.Body>
                <div className="d-flex justify-content-between mb-2">
                  <strong>{review.author}</strong>
                  <div>
                    {Array(review.author_details.rating)
                      .fill()
                      .map((_, i) => (i < 5 ? <span key={i}>‚≠ê</span> : null))}
                  </div>
                </div>
                <p>{review.content}</p>
              </Card.Body>
            </Card>

            
          ))}
          
        </>
        
      )}
      
    </Container>
  );
}
