import { useState, useEffect } from "react";
import { Form, Row, Col, Card, Spinner } from "react-bootstrap";
import { Link } from "react-router-dom";
import axios from "axios";

export default function Search() {
  const [query, setQuery] = useState("");
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(false);

  const setWatchlist = async (movieId) => {
    try {
      const token = localStorage.getItem("tokenMovie");
      const userId = JSON.parse(localStorage.getItem("user")).id;
      console.log("userId", userId);
      console.log("token", token);

      const optionsPost = {
        method: "POST",
        headers: {
          accept: "application/json",
          "content-type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          media_type: "movie",
          media_id: movieId,
          watchlist: true,
        }),
      };

      fetch(
        `https://api.themoviedb.org/3/account/${userId}/watchlist`,
        optionsPost
      )
        .then((res) => res.json())
        .then((res) => {
          console.log("abcccc");
          console.log("abcccc", res);
        })
        .catch((err) => {
          console.log("abcccc");
          console.log("abcccc", err);
        });
    } catch (err) {
      console.error(err);
    }
  };
  useEffect(() => {
    

    const timer = setTimeout(searchMovies, 500);
    return () => clearTimeout(timer);
  }, [query]);

  return (
    <div className="p-4">
      <Form className="mb-4" onSubmit={(e) => e.preventDefault()}>
        <Form.Control
          size="lg"
          type="search"
          placeholder="Search for movies..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          autoFocus
        />
      </Form>

      {loading && <Spinner animation="border" className="d-block mx-auto" />}

      {!loading && movies.length === 0 && query.length >= 3 && (
        <div className="text-center text-muted mb-4">
          No movies found for "{query}"
        </div>
      )}

      <Row xs={1} md={4} className="g-4">
        {movies?.map((movie) => (
          <Col key={movie.id}>
            <Card className="h-100 shadow-sm">
              <Card.Img
                variant="top"
                src={
                  movie.poster_path
                    ? `https://image.tmdb.org/t/p/w500${movie.poster_path}`
                    : "/placeholder.jpg"
                }
                style={{ height: "400px", objectFit: "cover" }}
              />
              <Card.Body className="d-flex flex-column">
                <Card.Title>{movie.title}</Card.Title>
                <Card.Text className="text-muted small mt-auto">
                  {movie.release_date &&
                    new Date(movie.release_date).getFullYear()}
                  {movie.vote_average &&
                    ` • ⭐ ${
                      movie.vote_average > 5 ? 5 : movie.vote_average.toFixed(1)
                    }`}
                </Card.Text>
                <Link
                  to={`/movie/${movie.id}`}
                  className="btn btn-primary w-100 mt-2"
                >
                  View Details
                </Link>
                <button
                  onClick={() => setWatchlist(movie.id)}
                  className="btn btn-primary w-100 mt-2"
                >
                  Add To Watchlist
                </button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </div>
  );
}
