import { useState, useEffect } from "react";
import { Form, Row, Col, Card, Spinner } from "react-bootstrap";
import { Link } from "react-router-dom";
import axios from "axios";

export default function Search() {
  const [query, setQuery] = useState("");
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(false);
  const [wathcListIds, setWatchlistIds] = useState([]);
  const [userId, setUserId] = useState([]);

  const sendWatchlist = async (movieId, isAddWishlist) => {
    try {
      const token = localStorage.getItem("tokenMovie");
      const userId = JSON.parse(localStorage.getItem("user")).id;
      console.log("userId", userId);
      console.log("token", token);

      const optionsPost = {
        method: "POST",
        headers: {
          accept: "application/json",
          "content-type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          media_type: "movie",
          media_id: movieId,
          watchlist: !isAddWishlist,
        }),
      };

      fetch(
        `https://api.themoviedb.org/3/account/${userId}/watchlist`,
        optionsPost
      )
        .then((res) => res.json())
        .then((res) => {
          searchMovies();
          console.log(res);
        })
        .catch((err) => {
          searchMovies();
          console.log(err);
        });
    } catch (err) {
      console.error(err);
    }
  };

  const searchMovies = async () => {
    setLoading(true);
    try {
      let token = localStorage.getItem("tokenMovie") || null;
      if (!token) {
        const response = await axios.post("/api/auth/reqToken");
        console.log("Token response:", response.data);
        localStorage.setItem("tokenMovie", response.data.token);
        token = response.data.token;
      }
      const options = {
        method: "GET",
        headers: {
          accept: "application/json",
          Authorization: `Bearer ${token}`,
        },
      };
      const userId = JSON.parse(localStorage.getItem("user"))
        ? JSON.parse(localStorage.getItem("user")).id
        : null;
      set

      let fetchUri =
        query.length < 1
          ? "movie/upcoming?language=en-US&page=1"
          : `search/movie?query=${query}&include_adult=false&language=en-US&page=1`;

      console.log("userId", userId);
      if (userId !== null) {
        fetch(
          `https://api.themoviedb.org/3/account/${userId}/watchlist/movies?language=en-US&page=1&sort_by=created_at.asc`,
          options
        )
          .then((res) => res.json())
          .then((res) => {
            if (res.results) {
              // get the movie ids from res.results and store in an array
              console.log(res);
              const watchlistMapId = res.results.map((movie) => movie.id);
              fetch(`https://api.themoviedb.org/3/${fetchUri}`, options)
                .then((res) => res.json())
                .then((res) => {
                  setWatchlistIds(watchlistMapId);
                  console.log(wathcListIds);
                  setMovies(res.results || []);
                })
                .catch((err) => console.error(err));
            }
          })
          .catch((err) => console.error(err));
      } else {
        fetch(`https://api.themoviedb.org/3/${fetchUri}`, options)
          .then((res) => res.json())
          .then((res) => {
            console.log(wathcListIds);
            setMovies(res.results || []);
          })
          .catch((err) => console.error(err));
      }
    } catch (err) {
      console.error("Search error:", err);
      setMovies([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const timer = setTimeout(searchMovies, 500);
    return () => clearTimeout(timer);
  }, [query]);

  return (
    <div className="p-4">
      <Form className="mb-4" onSubmit={(e) => e.preventDefault()}>
        <Form.Control
          size="lg"
          type="search"
          placeholder="Search for movies..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          autoFocus
        />
      </Form>

      {loading && <Spinner animation="border" className="d-block mx-auto" />}

      {!loading && movies.length === 0 && query.length >= 3 && (
        <div className="text-center text-muted mb-4">
          No movies found for "{query}"
        </div>
      )}

      <Row xs={1} md={4} className="g-4">
        {movies?.map((movie) => (
          <Col key={movie.id}>
            <Card className="h-100 shadow-sm">
              <Card.Img
                variant="top"
                src={
                  movie.poster_path
                    ? `https://image.tmdb.org/t/p/w500${movie.poster_path}`
                    : "/placeholder.jpg"
                }
                style={{ height: "400px", objectFit: "cover" }}
              />
              <Card.Body className="d-flex flex-column">
                <Card.Title>{movie.title}</Card.Title>
                <Card.Text className="text-muted small mt-auto">
                  {movie.release_date &&
                    new Date(movie.release_date).getFullYear()}
                  {movie.vote_average &&
                    ` • ⭐ ${
                      movie.vote_average > 5 ? 5 : movie.vote_average.toFixed(1)
                    }`}
                </Card.Text>
                <Link
                  to={`/movie/${movie.id}`}
                  className="btn btn-primary w-100 mt-2"
                >
                  View Details
                </Link>
                <button
                  onClick={() =>
                    sendWatchlist(movie.id, wathcListIds.includes(movie.id))
                  }
                  className={
                    wathcListIds.includes(movie.id)
                      ? "btn btn-warning w-100 mt-2"
                      : "btn btn-primary w-100 mt-2"
                  }
                >
                  {wathcListIds.includes(movie.id)
                    ? "Remove From Watchlist"
                    : "Add To Watchlist"}
                </button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </div>
  );
}
