import { Container } from "react-bootstrap";
import { useState, useEffect } from "react";
import { Row, Col, Card, Spinner } from "react-bootstrap";
import { Link } from "react-router-dom";
import axios from "axios";
const Home = () => {
  const [movies, setMovies] = useState([]);
  const [upcomingMovie, setUpcomingMovie] = useState([]);
  const [loading, setLoading] = useState(false);
  const [wathcListIds, setWatchlistIds] = useState([]);
  const [userId, setUserId] = useState([]);

  const sendWatchlist = async (movieId, isAddWishlist) => {
    try {
      const token = localStorage.getItem("tokenMovie");
      const userId = JSON.parse(localStorage.getItem("user")).id;
      console.log("userId", userId);
      console.log("token", token);

      const optionsPost = {
        method: "POST",
        headers: {
          accept: "application/json",
          "content-type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          media_type: "movie",
          media_id: movieId,
          watchlist: !isAddWishlist,
        }),
      };

      fetch(
        `https://api.themoviedb.org/3/account/${userId}/watchlist`,
        optionsPost
      )
        .then((res) => res.json())
        .then((res) => {
          searchMovies();
          console.log(res);
        })
        .catch((err) => {
          searchMovies();
          console.log(err);
        });
    } catch (err) {
      console.error(err);
    }
  };

  const searchMovies = async () => {
    setLoading(true);
    try {
      let token = localStorage.getItem("tokenMovie") || null;
      if (!token) {
        const response = await axios.post("/api/auth/reqToken");
        localStorage.setItem("tokenMovie", response.data.token);
        token = response.data.token;
      }
      const options = {
        method: "GET",
        headers: {
          accept: "application/json",
          Authorization: `Bearer ${token}`,
        },
      };
      const userIdVal = (JSON.parse(localStorage.getItem("user"))) ? JSON.parse(localStorage.getItem("user")).id : null;
      setUserId(userIdVal);
      if(userIdVal) {
        fetch(
          `https://api.themoviedb.org/3/account/${userIdVal}/watchlist/movies?language=en-US&page=1&sort_by=created_at.asc`,
          options
        )
          .then((res) => res.json())
          .then((res) => {
            if (res.results) {
              // get the movie ids from res.results and store in an array
              console.log(res);
              const watchlistMapId = res.results.map((movie) => movie.id);
              setWatchlistIds(watchlistMapId);
              searchTopRatedMovies(options);
              searchPopularMovies(options);
            }
          })
          .catch((err) => console.error(err));
        } else {
          
        }
    } catch (err) {
      console.error("Search error:", err);
      setUpcomingMovie([]);
    } finally {
      setLoading(false);
    }
  };

  const searchPopularMovies = async (options) => {
    try {
      fetch(
        `https://api.themoviedb.org/3/movie/popular?language=en-US&page=1`,
        options
      )
        .then((res) => res.json())
        .then((res) => {
          if (res.results.length > 8) {
            res.results = res.results.slice(0, 8); // Limit to 8 movies
          }
          setMovies(res.results || []);
        })
        .catch((err) => console.error(err));
    } catch (err) {
      console.error("Search error:", err);
      setMovies([]);
    }
  };

  const searchTopRatedMovies = async (options) => {
    try {
      fetch(
        `https://api.themoviedb.org/3/movie/top_rated?language=en-US&page=1`,
        options
      )
        .then((res) => res.json())
        .then((res) => {
          if (res.results.length > 8) {
            res.results = res.results.slice(0, 8); // Limit to 8 movies
          }
          setUpcomingMovie(res.results || []);
        })
        .catch((err) => console.error(err));
    } catch (err) {
      console.error("Search error:", err);
      setUpcomingMovie([]);
    }
  };

  useEffect(() => {
    const timer = setTimeout(searchMovies, 500);
    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="p-4">
      <Container className="text-center mt-5">
        <h1>Welcome to Cinerate</h1>
        <p className="lead">Discover, rate, and review your favorite movies</p>
      </Container>
      {loading && <Spinner animation="border" className="d-block mx-auto" />}

      {!loading && movies.length === 0 && (
        <div className="text-center text-muted mb-4">No movies found.</div>
      )}
      <Container className="text-center mt-5">
        <h2>Popular Movies</h2>
        <p className="lead">Top 8 Popular Movies</p>
      </Container>
      <Row xs={1} md={4} className="g-4">
        {movies?.map((movie) => (
          <Col key={movie.id}>
            <Card className="h-100 shadow-sm">
              <Card.Img
                variant="top"
                src={
                  movie.poster_path
                    ? `https://image.tmdb.org/t/p/w500${movie.poster_path}`
                    : "/placeholder.jpg"
                }
                style={{ height: "400px", objectFit: "cover" }}
              />
              <Card.Body className="d-flex flex-column">
                <Card.Title>{movie.title}</Card.Title>
                <Card.Text className="text-muted small mt-auto">
                  {movie.release_date &&
                    new Date(movie.release_date).getFullYear()}
                  {movie.vote_average &&
                    ` • ⭐ ${
                      movie.vote_average > 5 ? 5 : movie.vote_average.toFixed(1)
                    }`}
                </Card.Text>
                <Link
                  to={`/movie/${movie.id}`}
                  className="btn btn-primary w-100 mt-2"
                >
                  View Details
                </Link>
                <button
                  onClick={() =>
                    sendWatchlist(movie.id, wathcListIds.includes(movie.id))
                  }
                  className={
                    wathcListIds.includes(movie.id)
                      ? "btn btn-warning w-100 mt-2"
                      : "btn btn-primary w-100 mt-2"
                  }
                >
                  {wathcListIds.includes(movie.id)
                    ? "Remove From Watchlist"
                    : "Add To Watchlist"}
                </button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>

      <Container className="text-center mt-5">
        <h2>Top Rated Movies</h2>
        <p className="lead">Top Picks for You</p>
      </Container>
      <Row xs={1} md={4} className="g-4">
        {upcomingMovie?.map((movie) => (
          <Col key={movie.id}>
            <Card className="h-100 shadow-sm">
              <Card.Img
                variant="top"
                src={
                  movie.poster_path
                    ? `https://image.tmdb.org/t/p/w500${movie.poster_path}`
                    : "/placeholder.jpg"
                }
                style={{ height: "400px", objectFit: "cover" }}
              />
              <Card.Body className="d-flex flex-column">
                <Card.Title>{movie.title}</Card.Title>
                <Card.Text className="text-muted small mt-auto">
                  {movie.release_date &&
                    new Date(movie.release_date).getFullYear()}
                  {movie.vote_average &&
                    ` • ⭐ ${
                      movie.vote_average > 5 ? 5 : movie.vote_average.toFixed(1)
                    }`}
                </Card.Text>
                <Link
                  to={`/movie/${movie.id}`}
                  className="btn btn-primary w-100 mt-2"
                >
                  View Details
                </Link>
                <button
                  onClick={() =>
                    sendWatchlist(movie.id, wathcListIds.includes(movie.id))
                  }
                  className={
                    wathcListIds.includes(movie.id)
                      ? "btn btn-warning w-100 mt-2"
                      : "btn btn-primary w-100 mt-2"
                  }
                >
                  {wathcListIds.includes(movie.id)
                    ? "Remove From Watchlist"
                    : "Add To Watchlist"}
                </button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </div>
  );
};

export default Home;
