import React, { useState } from "react";
import { Form, Button, Alert, Card, Spinner } from "react-bootstrap";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

const Register = () => {
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    password: "",
    confirmPassword: "",
  });
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setLoading(true);

    try {
      const response = await axios.post(
        "http://localhost:5000/api/auth/register",
        formData
      );

      if (response.data.success) {
        // Store token and user data
        localStorage.setItem("token", response.data.token);
        localStorage.setItem("user", JSON.stringify(response.data.user));

        // Redirect to profile
        navigate("/profile");
      } else {
        setError(response.data.errors.join(", ") || "Registration failed");
      }
    } catch (err) {
      const serverError = err.response?.data?.errors || ["Registration failed"];
      setError(serverError.join(", "));
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  return (
    <div className="d-flex justify-content-center align-items-center min-vh-100 bg-light">
      <Card
        className="shadow-lg border-0 rounded"
        style={{ maxWidth: "400px", width: "95%" }}
      >
        <Card.Header className="bg-primary text-white text-center py-2">
          <h3 className="fw-bold m-0">Create Account</h3>
        </Card.Header>
        <Card.Body className="p-3">
          {error && (
            <Alert variant="danger" className="py-2 mb-3 small">
              <i className="bi bi-exclamation-triangle-fill me-1"></i>
              {error}
            </Alert>
          )}

          <Form onSubmit={handleSubmit} className="compact-form">
            <Form.Group className="mb-2" controlId="formFullName">
              <Form.Label className="fw-medium small mb-1">Fullname</Form.Label>
              <Form.Control
                type="text"
                name="fullName"
                required
                value={formData.fullName}
                onChange={handleChange}
                placeholder="Enter full name"
                className="py-1 shadow-sm"
                size="sm"
              />
            </Form.Group>

            <Form.Group className="mb-2" controlId="formUsername">
              <Form.Label className="fw-medium small mb-1">Username</Form.Label>
              <Form.Control
                type="text"
                name="username"
                required
                value={formData.username}
                onChange={handleChange}
                placeholder="Enter username"
                className="py-1 shadow-sm"
                size="sm"
              />
            </Form.Group>

            <Form.Group className="mb-2" controlId="formEmail">
              <Form.Label className="fw-medium small mb-1">Email</Form.Label>
              <Form.Control
                type="email"
                name="email"
                required
                value={formData.email}
                onChange={handleChange}
                placeholder="Enter email"
                className="py-1 shadow-sm"
                size="sm"
              />
            </Form.Group>

            <Form.Group className="mb-2" controlId="formPassword">
              <Form.Label className="fw-medium small mb-1">Password</Form.Label>
              <Form.Control
                type="password"
                name="password"
                required
                value={formData.password}
                onChange={handleChange}
                placeholder="Enter password (min 6 characters)"
                className="py-1 shadow-sm"
                size="sm"
              />
              <Form.Text className="text-muted x-small">
                Min 6 characters
              </Form.Text>
            </Form.Group>

            <Form.Group className="mb-3" controlId="formConfirmPassword">
              <Form.Label className="fw-medium small mb-1">
                Confirm Password
              </Form.Label>
              <Form.Control
                type="password"
                name="confirmPassword"
                required
                value={formData.confirmPassword}
                onChange={handleChange}
                placeholder="Confirm password"
                className="py-1 shadow-sm"
                size="sm"
              />
            </Form.Group>

            <Button
              variant="primary"
              type="submit"
              className="w-100 py-1 mb-2 fw-medium"
              disabled={loading}
              size="sm"
            >
              {loading ? (
                <>
                  <Spinner
                    as="span"
                    animation="border"
                    size="sm"
                    role="status"
                    aria-hidden="true"
                    className="me-1"
                    style={{ width: "0.8rem", height: "0.8rem" }}
                  />
                  Creating Account...
                </>
              ) : (
                "Register"
              )}
            </Button>

            <div className="text-center mt-2">
              <small>
                Already have an account?{" "}
                <Link
                  to="/login"
                  className="text-primary text-decoration-none fw-bold"
                >
                  Log In
                </Link>
              </small>
            </div>
          </Form>
        </Card.Body>
        <Card.Footer className="text-center py-2 bg-light">
          <span className="x-small text-muted">
            By registering, you agree to our Terms of Service
          </span>
        </Card.Footer>
      </Card>
    </div>
  );
};

export default Register;
