import { Container } from "react-bootstrap";
import { useState, useEffect } from "react";
import { Row, Col, Card, Spinner, Badge } from "react-bootstrap";
import { Link } from "react-router-dom";
import axios from "axios";

const Home = () => {
  const [movies, setMovies] = useState([]);
  const [upcomingMovie, setUpcomingMovie] = useState([]);
  const [loading, setLoading] = useState(false);
  const [watchListIds, setWatchlistIds] = useState([]);
  const [userId, setUserId] = useState(null);

  const sendWatchlist = async (movieId, isAddWishlist) => {
    try {
      const token = localStorage.getItem("tokenMovie");
      const userId = JSON.parse(localStorage.getItem("user")).id;
      console.log("userId", userId);
      console.log("token", token);

      const optionsPost = {
        method: "POST",
        headers: {
          accept: "application/json",
          "content-type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          media_type: "movie",
          media_id: movieId,
          watchlist: !isAddWishlist,
        }),
      };

      fetch(
        `https://api.themoviedb.org/3/account/${userId}/watchlist`,
        optionsPost
      )
        .then((res) => res.json())
        .then((res) => {
          let currentUserWatchList = [];
          if (!isAddWishlist) {
            let movie = {
              userid: userId,
              id: movieId,
            };

            if (localStorage.getItem(`user_watchList_${userId}`)) {
              currentUserWatchList = JSON.parse(
                localStorage.getItem(`user_watchList_${userId}`)
              );
            }
            currentUserWatchList.push(movie);
            localStorage.setItem(
              `user_watchList_${userId}`,
              JSON.stringify(currentUserWatchList)
            );
          } else {
            // remove`user_watchlist_${userId}`from storage
            currentUserWatchList = JSON.parse(
              localStorage.getItem(`user_watchList_${userId}`)
            );
            console.log(currentUserWatchList);
            currentUserWatchList = currentUserWatchList.filter(
              (obj) => obj.userid === userId && obj.id !== movieId
            );
            localStorage.setItem(
              `user_watchList_${userId}`,
              JSON.stringify(currentUserWatchList)
            );
          }
          searchMovies();
        })
        .catch((err) => {
          searchMovies();
          console.log(err);
        });
    } catch (err) {
      console.error(err);
    }
  };

  // const searchMovies = async () => {
  //   setLoading(true);
  //   try {
  //     let token = localStorage.getItem("tokenMovie") || null;
  //     if (!token) {
  //       const response = await axios.post("/api/auth/reqToken");
  //       localStorage.setItem("tokenMovie", response.data.token);
  //       token = response.data.token;
  //     }
  //     const options = {
  //       method: "GET",
  //       headers: {
  //         accept: "application/json",
  //         Authorization: `Bearer ${token}`,
  //       },
  //     };
  //     const userIdVal = JSON.parse(localStorage.getItem("user"))
  //       ? JSON.parse(localStorage.getItem("user")).id
  //       : null;
  //     setUserId(userIdVal);
  //     if (userIdVal) {
  //       fetch(
  //         `https://api.themoviedb.org/3/account/${userIdVal}/watchlist/movies?language=en-US&page=1&sort_by=created_at.asc`,
  //         options
  //       )
  //         .then((res) => res.json())
  //         .then((res) => {
  //           if (res.results) {
  //             // get the movie ids from res.results and store in an array
  //             console.log(res);
  //             let watchlistMapId = [];
  //             const userWatchList = localStorage.getItem(
  //               `user_watchList_${userId}`
  //             );
  //             if (userWatchList) {
  //               const currentUserWatchList = JSON.parse(userWatchList);
  //               watchlistMapId = currentUserWatchList.map((watchList) => {
  //                 if (watchList.userid === userIdVal) {
  //                   watchlistMapId.push(watchList.id);
  //                 }
  //                 setWatchlistIds(watchlistMapId);
  //               });
  //             } else {
  //               setWatchlistIds([]);
  //             }
  //             searchTopRatedMovies(options);
  //             searchPopularMovies(options);
  //           }
  //         })
  //         .catch((err) => console.error(err));
  //     } else {
  //       searchTopRatedMovies(options);
  //       searchPopularMovies(options);
  //     }
  //   } catch (err) {
  //     console.error("Search error:", err);
  //     setUpcomingMovie([]);
  //   } finally {
  //     setLoading(false);
  //   }
  // };

  const searchMovies = async () => {
    setLoading(true);
    try {
      let token = localStorage.getItem("tokenMovie") || null;
      if (!token) {
        const response = await axios.post("/api/auth/reqToken");
        localStorage.setItem("tokenMovie", response.data.token);
        token = response.data.token;
      }
      const options = {
        method: "GET",
        headers: {
          accept: "application/json",
          Authorization: `Bearer ${token}`,
        },
      };
      const userIdVal = JSON.parse(localStorage.getItem("user"))
        ? JSON.parse(localStorage.getItem("user")).id
        : null;
      setUserId(userIdVal);
      if (userIdVal) {
        fetch(
          `https://api.themoviedb.org/3/account/${userIdVal}/watchlist/movies?language=en-US&page=1&sort_by=created_at.asc`,
          options
        )
          .then((res) => res.json())
          .then((res) => {
            if (res.results) {
              let watchlistMapId = [];
              const userWatchList = localStorage.getItem(
                `user_watchList_${userIdVal}` // Use userIdVal instead of userId
              );
              if (userWatchList) {
                const currentUserWatchList = JSON.parse(userWatchList);
                currentUserWatchList.forEach((watchList) => {
                  // Use forEach instead of map
                  if (watchList.userid === userIdVal) {
                    watchlistMapId.push(watchList.id);
                  }
                });
                setWatchlistIds(watchlistMapId);
              } else {
                setWatchlistIds([]);
              }
              searchTopRatedMovies(options);
              searchPopularMovies(options);
            }
          })
          .catch((err) => console.error(err));
      } else {
        searchTopRatedMovies(options);
        searchPopularMovies(options);
      }
    } catch (err) {
      console.error("Search error:", err);
      setUpcomingMovie([]);
    } finally {
      setLoading(false);
    }
  };

  const searchPopularMovies = async (options) => {
    try {
      fetch(
        `https://api.themoviedb.org/3/movie/popular?language=en-US&page=1`,
        options
      )
        .then((res) => res.json())
        .then((res) => {
          if (res.results.length > 8) {
            res.results = res.results.slice(0, 8); // Limit to 8 movies
          }
          getMovieGenre(options, res, 0);
        })
        .catch((err) => console.error(err));
    } catch (err) {
      console.error("Search error:", err);
      setMovies([]);
    }
  };

  const searchTopRatedMovies = async (options) => {
    try {
      fetch(
        `https://api.themoviedb.org/3/movie/top_rated?language=en-US&page=1`,
        options
      )
        .then((res) => res.json())
        .then((res) => {
          if (res.results.length > 8) {
            res.results = res.results.slice(0, 8); // Limit to 8 movies
          }
          getMovieGenre(options, res, 1);
        })
        .catch((err) => console.error(err));
    } catch (err) {
      console.error("Search error:", err);
      setUpcomingMovie([]);
    }
  };

  const getMovieGenre = async (options, moviesList, movieType) => {
    try {
      fetch(
        "https://api.themoviedb.org/3/genre/movie/list?language=en",
        options
      )
        .then((res) => res.json())
        .then((resGenre) => {
          // map res.results to check genre
          moviesList.results.map((movie) => {
            let genresMovie = [];
            movie.genre_ids.map((genreId) => {
              resGenre.genres.map((genreItem) => {
                if (genreItem.id === genreId) {
                  genresMovie.push(genreItem.name);
                }
              });
            });
            movie.genres = genresMovie;
          });
          if (movieType === 1) {
            setUpcomingMovie(moviesList.results || []);
          } else {
            setMovies(moviesList.results || []);
          }
        })
        .catch((err) => console.error(err));
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    const timer = setTimeout(searchMovies, 500);
    return () => clearTimeout(timer);
  }, []);

  // Get color based on rating
  const getRatingColor = (rating) => {
    if (rating >= 4) return "success";
    if (rating >= 3) return "info";
    if (rating >= 2) return "warning";
    return "danger";
  };
  return (
    <div className="bg-light min-vh-100">
      {/* Hero Section */}
      <div className="position-relative overflow-hidden p-5 text-center" style={{
                backgroundImage:
                  "linear-gradient(135deg, #4158D0 0%, #C850C0 46%, #FFCC70 100%)",
                opacity: 0.9,
              }}>
        <div className="col-lg-8 mx-auto py-5">
          <h1 className="display-4 fw-bold mb-3">Welcome to Cinerate</h1>
          <p className="lead mb-4 opacity-85">
            Discover, rate, and review your favorite movies from our curated
            collection
          </p>
          <div className="d-flex gap-3 justify-content-center">
            <a href="#popular" className="btn btn-light btn-lg px-4 fw-bold">
              Popular Movies
            </a>
            <a href="#toprated" className="btn btn-outline-light btn-lg px-4">
              Top Rated
            </a>
          </div>
        </div>
      </div>

      {/* Loading State */}
      {loading && (
        <div className="d-flex justify-content-center align-items-center py-5 my-5">
          <div className="text-center">
            <Spinner animation="border" variant="primary" className="fs-4" />
            <p className="mt-3 text-muted fs-5">
              Loading amazing movies for you...
            </p>
          </div>
        </div>
      )}

      {!loading && movies.length === 0 && (
        <div className="text-center py-5 my-5">
          <i className="bi bi-film fs-1 text-secondary mb-3" />
          <h3 className="text-secondary mb-2">No movies found</h3>
          <p className="text-muted fs-5">
            Please try again later or check your connection
          </p>
        </div>
      )}

      {/* Popular Movies Section */}
      <Container id="popular" className="py-5">
        <div className="d-flex align-items-center mb-4">
          <div
            className="bg-primary me-3"
            style={{ width: "4px", height: "32px" }}
          />
          <h2 className="h2 fw-bold m-0 text-dark">Popular Movies</h2>
        </div>
        <p className="lead text-muted mb-4">The most watched films right now</p>

        <Row xs={1} sm={2} md={2} lg={4} className="g-4">
          {movies?.map((movie) => (
            <Col key={movie.id}>
              <Card className="h-100 border-0 shadow-sm movie-card">
                <div className="position-relative overflow-hidden ratio ratio-2x3">
                  <Card.Img
                    variant="top"
                    src={
                      movie.poster_path
                        ? `https://image.tmdb.org/t/p/w500${movie.poster_path}`
                        : "/placeholder.jpg"
                    }
                    className="img-fluid"
                  />
                  <div className="movie-overlay d-flex align-items-end p-3">
                    <div className="w-100">
                      <div className="d-flex justify-content-between align-items-center mb-2">
                        <Badge
                          bg="red"
                          text="dark"
                          className="rounded-pill px-3 py-2"
                        >
                          ★{" "}
                          {movie.vote_average > 5
                            ? 5
                            : movie.vote_average.toFixed(1)}
                        </Badge>
                        <small className="text-white opacity-85">
                          {movie.release_date &&
                            new Date(movie.release_date).getFullYear()}
                        </small>
                      </div>
                      {userId && (
                        <button
                          onClick={(e) => {
                            e.preventDefault();
                            sendWatchlist(
                              movie.id,
                              watchListIds.includes(movie.id)
                            );
                          }}
                          className={`btn btn-sm w-100 mb-2 ${
                            watchListIds.includes(movie.id)
                              ? "btn-outline-danger"
                              : "btn-outline-primary"
                          }`}
                        >
                          {watchListIds.includes(movie.id)
                            ? "Remove Watchlist"
                            : "Add to Watchlist"}
                        </button>
                      )}
                      <Link
                        to={`/movie/${movie.id}`}
                        className="btn btn-primary btn-sm w-100"
                      >
                        View Details
                      </Link>
                    </div>
                  </div>
                </div>
                <Card.Body className="p-3">
                  <Card.Title
                    className="fw-bold mb-2"
                    style={{ minHeight: "3rem" }}
                  >
                    {movie.title}
                  </Card.Title>
                  <div className="d-flex flex-wrap gap-2">
                    {movie.genres?.slice(0, 3).map((genre, idx) => (
                      <Badge
                        key={idx}
                        bg="light"
                        text="dark"
                        className="rounded-pill font-monospace border"
                        style={{
                          fontSize: "0.75rem",
                          padding: "0.375rem 0.75rem",
                        }}
                      >
                        {genre}
                      </Badge>
                    ))}
                  </div>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>

      {/* Top Rated Movies Section */}
      <div className="bg-light py-5">
        <Container id="toprated">
          <div className="d-flex align-items-center mb-4">
            <div
              className="bg-success me-3"
              style={{ width: "4px", height: "32px" }}
            />
            <h2 className="h2 fw-bold m-0 text-dark">Top Rated Movies</h2>
          </div>
          <p className="lead text-muted mb-4">
            Critically acclaimed masterpieces
          </p>

          <Row xs={1} sm={2} md={2} lg={4} className="g-4">
            {upcomingMovie?.map((movie) => (
              <Col key={movie.id}>
                <Card className="h-100 border-0 shadow-sm movie-card">
                  <div className="position-relative overflow-hidden ratio ratio-2x3">
                    <Card.Img
                      variant="top"
                      src={
                        movie.poster_path
                          ? `https://image.tmdb.org/t/p/w500${movie.poster_path}`
                          : "/placeholder.jpg"
                      }
                      className="img-fluid"
                    />
                    <div className="movie-overlay d-flex align-items-end p-3">
                      <div className="w-100">
                        <div className="d-flex justify-content-between align-items-center mb-2">
                          <Badge
                            bg="white"
                            text="dark"
                            className="rounded-pill px-3 py-2"
                          >
                            ★{" "}
                            {movie.vote_average > 5
                              ? 5
                              : movie.vote_average.toFixed(1)}
                          </Badge>
                          <small className="text-white opacity-85">
                            {movie.release_date &&
                              new Date(movie.release_date).getFullYear()}
                          </small>
                        </div>
                        {userId && (
                          <button
                            onClick={(e) => {
                              e.preventDefault();
                              sendWatchlist(
                                movie.id,
                                watchListIds.includes(movie.id)
                              );
                            }}
                            className={`btn btn-sm w-100 mb-2 ${
                              watchListIds.includes(movie.id)
                                ? "btn-outline-danger"
                                : "btn-outline-primary"
                            }`}
                          >
                            {watchListIds.includes(movie.id)
                              ? "Remove Watchlist"
                              : "Add to Watchlist"}
                          </button>
                        )}
                        <Link
                          to={`/movie/${movie.id}`}
                          className="btn btn-primary btn-sm w-100"
                        >
                          View Details
                        </Link>
                      </div>
                    </div>
                  </div>
                  <Card.Body className="p-3">
                    <Card.Title
                      className="fw-bold mb-2"
                      style={{ minHeight: "3rem" }}
                    >
                      {movie.title}
                    </Card.Title>
                    <div className="d-flex flex-wrap gap-2">
                      {movie.genres?.slice(0, 3).map((genre, idx) => (
                        <Badge
                          key={idx}
                          bg="light"
                          text="dark"
                          className="rounded-pill font-monospace border"
                          style={{
                            fontSize: "0.75rem",
                            padding: "0.375rem 0.75rem",
                          }}
                        >
                          {genre}
                        </Badge>
                      ))}
                    </div>
                  </Card.Body>
                </Card>
              </Col>
            ))}
          </Row>
        </Container>
      </div>

      <style jsx="true">{`
        .movie-card {
          transition: all 0.3s ease;
          border-radius: 12px;
          overflow: hidden;
          background: white;
        }
        .movie-card:hover {
          transform: translateY(-8px);
          box-shadow: 0 15px 30px rgba(0, 0, 0, 0.1) !important;
        }
        .movie-overlay {
          position: absolute;
          bottom: 0;
          left: 0;
          right: 0;
          background: linear-gradient(transparent, rgba(0, 0, 0, 0.7));
          opacity: 0;
          transition: opacity 0.3s ease;
          padding: 1.5rem;
        }
        .movie-card:hover .movie-overlay {
          opacity: 1;
        }
        .ratio-2x3::before {
          padding-top: 150%;
        }
      `}</style>
    </div>
  );
};

export default Home;
