import React, { useEffect, useState } from "react";
import {
  ListGroup,
  Card,
  Spinner,
  Row,
  Col,
  Alert,
  Badge,
  Container,
  Button,
} from "react-bootstrap";
import { Link } from "react-router-dom";
import axios from "axios";
import { StarFill } from "react-bootstrap-icons";

export default function Profile() {
  const [user, setUser] = useState({});
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [watchlist, setWatchlistData] = useState([]);

  const getWatchList = async () => {
    setLoading(true);
    try {
      let token = localStorage.getItem("tokenMovie") || null;
      if (!token) {
        const response = await axios.post("/api/auth/reqToken");
        console.log("Token response:", response.data);
        localStorage.setItem("tokenMovie", response.data.token);
        token = response.data.token;
      }
      const options = {
        method: "GET",
        headers: {
          accept: "application/json",
          Authorization: `Bearer ${token}`,
        },
      };
      const userId = JSON.parse(localStorage.getItem("user")).id;

      console.log("userId", userId);

      fetch(
        `https://api.themoviedb.org/3/account/${userId}/watchlist/movies?language=en-US&page=1&sort_by=created_at.asc`,
        options
      )
        .then((res) => res.json())
        .then((res) => {
          console.log(res);
          setUser(JSON.parse(localStorage.getItem("user")));
          getMovieGenre(options, res);
          if (localStorage.getItem("reviewed_movies")) {
            fetchReviewedMovie(
              JSON.parse(localStorage.getItem("reviewed_movies")),
              options,
              userId
            );
          }
          setLoading(false);
        })
        .catch((err) => console.error(err));
    } catch (err) {
      console.error("Search error:", err);
      setWatchlistData([]);
    } finally {
      setLoading(false);
    }
  };

  const getMovieGenre = async (options, moviesList) => {
    try {
      fetch(
        "https://api.themoviedb.org/3/genre/movie/list?language=en",
        options
      )
        .then((res) => res.json())
        .then((resGenre) => {
          // map res.results to check genre
          moviesList.results.map((movie) => {
            let genresMovie = [];
            movie.genre_ids.map((genreId) => {
              resGenre.genres.map((genreItem) => {
                if (genreItem.id === genreId) {
                  genresMovie.push(genreItem.name);
                }
              });
            });
            movie.genres = genresMovie;
          });
          setWatchlistData(moviesList.results || []);
        })
        .catch((err) => console.error(err));
    } catch (err) {
      console.error(err);
    }
  };

  const removeWatchList = async (movieId) => {
    try {
      const token = localStorage.getItem("tokenMovie");
      const userId = JSON.parse(localStorage.getItem("user")).id;
      console.log("userId", userId);

      const optionsPost = {
        method: "POST",
        headers: {
          accept: "application/json",
          "content-type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          media_type: "movie",
          media_id: movieId,
          watchlist: false,
        }),
      };

      fetch(
        `https://api.themoviedb.org/3/account/${userId}/watchlist`,
        optionsPost
      )
        .then((res) => res.json())
        .then((res) => {
          getWatchList();
        })
        .catch((err) => {
          console.log(err);
          getWatchList();
        });
    } catch (err) {
      console.error(err);
    }
  };

  const fetchReviewedMovie = async (reviewedMovies, options, userId) => {
    let movieData = [];
    await reviewedMovies.map(async (movie) => {
      if (movie.userid === userId) {
        await fetch(
          `https://api.themoviedb.org/3/movie/${movie.id}?language=en-US`,
          options
        )
          .then((res) => res.json())
          .then((res) => {
            res.review = movie.review;
            res.rating = movie.rating;
            res.comment = movie.review;
            movieData.push(res);
            console.log("reviewwww", res);
          })
          .catch((err) => console.error(err));
      }
    });
    setReviews(movieData);
  };
  useEffect(() => {
    getWatchList();
  }, []);

  if (loading)
    return <Spinner animation="border" className="d-block mx-auto mt-5" />;

  return (
    <Container className="py-4">
      {error && (
        <Alert variant="danger" className="text-center">
          {error}
        </Alert>
      )}

      {/* Profile Header */}
      <Card className="mb-4 shadow">
        <Card.Body className="text-center">
          <div className="mb-3">
            <span
              className="profile-avatar bg-primary text-white rounded-circle d-inline-flex align-items-center justify-content-center"
              style={{ width: "80px", height: "80px", fontSize: "2rem" }}
            >
              {user.username?.charAt(0).toUpperCase()}
            </span>
          </div>
          <Card.Title className="h2 mb-1">{user.fullName}</Card.Title>
          <Card.Subtitle className="text-muted mb-3">
            @{user.username}
          </Card.Subtitle>
          <div className="d-flex justify-content-center gap-3">
            <Badge bg="light" text="dark" className="p-2">
              <span className="h5 mb-0">{watchlist.length}</span>
              <span className="d-block small">Watchlist</span>
            </Badge>
            <Badge bg="light" text="dark" className="p-2">
              <span className="h5 mb-0">{reviews.length}</span>
              <span className="d-block small">Reviews</span>
            </Badge>
          </div>
        </Card.Body>
      </Card>

      {/* Reviews Section */}
      <section className="mb-5">
        <h3 className="mb-4 border-bottom pb-2">My Reviews</h3>
        {reviews.length === 0 ? (
          <Alert variant="info" className="text-center">
            No reviews yet. Start reviewing movies!
          </Alert>
        ) : (
          <Row xs={1} md={2} lg={3} className="g-4">
            {reviews.map((review) => (
              <Col key={review.title}>
                <Card className="h-100 shadow-sm">
                  <Card.Img
                    variant="top"
                    src={
                      review.poster_path
                        ? `https://image.tmdb.org/t/p/w500${review.poster_path}`
                        : "/placeholder.jpg"
                    }
                    alt={review.title}
                    style={{ height: "200px", objectFit: "cover" }}
                  />
                  <Card.Body className="d-flex flex-column">
                    <Card.Title>
                      <Link
                        to={`/movie/${review.id}`}
                        className="text-decoration-none"
                      >
                        {review.title}
                      </Link>
                    </Card.Title>
                    <div className="mb-2">
                      {[...Array(5)].map((_, i) => (
                        <StarFill
                          key={i}
                          color={i < review.rating ? "#ffc107" : "#e4e5e9"}
                          className="me-1"
                        />
                      ))}
                    </div>
                    <Card.Text className="flex-grow-1">
                      {review.comment}
                    </Card.Text>
                    {review.genres && (
                      <div className="mt-auto">
                        <small className="text-muted">
                          {review.genres.join(", ")}
                        </small>
                      </div>
                    )}
                  </Card.Body>
                </Card>
              </Col>
            ))}
          </Row>
        )}
      </section>

      {/* Watchlist Section */}
      <section>
        <div className="d-flex justify-content-between align-items-center mb-4">
          <h3 className="mb-0">My Watchlist</h3>
          <Badge pill bg="secondary">
            {watchlist.length} items
          </Badge>
        </div>

        {watchlist.length === 0 ? (
          <Alert variant="info" className="text-center">
            Your watchlist is empty. Start adding movies!
          </Alert>
        ) : (
          <Row xs={2} md={4} lg={5} className="g-4">
            {watchlist.map((movie) => (
              <Col key={movie.id}>
                <Card className="h-100 shadow-sm hover-effect">
                  <Card.Img
                    variant="top"
                    src={
                      movie.poster_path
                        ? `https://image.tmdb.org/t/p/w500${movie.poster_path}`
                        : "/placeholder.jpg"
                    }
                    alt={movie.title}
                    style={{ height: "250px", objectFit: "cover" }}
                  />
                  <Card.Body className="d-flex flex-column">
                    <Card.Title className="h6 mb-2">{movie.title}</Card.Title>
                    <div className="mt-auto">
                      <div className="d-flex justify-content-between small text-muted mb-2">
                        <span>
                          {movie.release_date?.split("-")[0] || "N/A"}
                        </span>
                        <span>
                          <StarFill className="text-warning me-1" />
                          {movie.vote_average?.toFixed(1) || "N/A"}
                        </span>
                      </div>
                      <Button
                        as={Link}
                        to={`/movie/${movie.id}`}
                        variant="outline-primary"
                        size="sm"
                        className="w-100 mb-2"
                      >
                        View Details
                      </Button>
                      <Button
                        variant="outline-danger"
                        size="sm"
                        className="w-100"
                        onClick={() => removeWatchList(movie.id)}
                      >
                        Remove from Watchlist
                      </Button>
                    </div>
                  </Card.Body>
                </Card>
              </Col>
            ))}
          </Row>
        )}
      </section>

      {loading && (
        <div className="text-center my-5">
          <Spinner animation="border" role="status">
            <span className="visually-hidden">Loading...</span>
          </Spinner>
        </div>
      )}
    </Container>
  );
}
