import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import {
  Container,
  Card,
  Form,
  Button,
  Row,
  Col,
  Spinner,
  Alert,
} from "react-bootstrap";
import axios from "axios";
import { useAuth } from "../context/AuthContext";

export default function Movie() {
  const { id } = useParams();
  const { user } = useAuth();
  const [movie, setMovie] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [rating, setRating] = useState(5);
  const [comment, setComment] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [movieRes, reviewsRes] = await Promise.all([
          axios.get(`/api/movies/${id}`),
          axios.get(`/api/reviews/movie/${id}`),
        ]);

        setMovie(movieRes.data);
        setReviews(reviewsRes.data);
      } catch (err) {
        setError("Failed to load movie data");
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [id]);

  const submitReview = async () => {
    if (!user) return;
    if (comment.length > 500) {
      setError("Comment must be less than 500 characters");
      return;
    }

    setSubmitting(true);
    try {
      await axios.post(
        "/api/reviews",
        { movieId: movie._id, rating, comment },
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      );

      const reviewsRes = await axios.get(`/api/reviews/movie/${movie._id}`);
      setReviews(reviewsRes.data);
      setComment("");
      setRating(5);
      setError("");
    } catch (err) {
      setError(err.response?.data?.error || "Error submitting review");
    } finally {
      setSubmitting(false);
    }
  };

  if (loading)
    return <Spinner animation="border" className="d-block mx-auto mt-5" />;
  if (error)
    return (
      <Alert variant="danger" className="mt-4">
        {error}
      </Alert>
    );

  return (
    <Container className="py-4">
      {movie && (
        <>
          <Row className="mb-4">
            <Col md={4}>
              <img
                src={`https://image.tmdb.org/t/p/w500${movie.posterPath}`}
                alt={movie.title}
                className="img-fluid rounded shadow"
              />
            </Col>
            <Col md={8}>
              <h1>{movie.title}</h1>
              <p className="lead">{movie.overview}</p>
              <div className="mb-3">
                <span className="fw-bold">Release Date:</span>{" "}
                {new Date(movie.releaseDate).toLocaleDateString()}
              </div>
            </Col>
          </Row>

          {user && (
            <Card className="mb-4">
              <Card.Body>
                <Card.Title>Write a Review</Card.Title>
                <Form>
                  <Form.Group className="mb-3">
                    <Form.Label>Rating</Form.Label>
                    <Form.Select
                      value={rating}
                      onChange={(e) => setRating(Number(e.target.value))}
                      disabled={submitting}
                    >
                      {[5, 4, 3, 2, 1].map((num) => (
                        <option key={num} value={num}>
                          {num} Stars
                        </option>
                      ))}
                    </Form.Select>
                  </Form.Group>
                  <Form.Group className="mb-3">
                    <Form.Control
                      as="textarea"
                      rows={3}
                      placeholder="Your review..."
                      value={comment}
                      onChange={(e) => setComment(e.target.value)}
                      disabled={submitting}
                    />
                  </Form.Group>
                  <Button
                    variant="primary"
                    onClick={submitReview}
                    disabled={submitting}
                  >
                    {submitting ? "Submitting..." : "Submit Review"}
                  </Button>
                </Form>
              </Card.Body>
            </Card>
          )}

          <h3 className="mb-3">User Reviews</h3>
          {reviews.map((review) => (
            <Card key={review._id} className="mb-3">
              <Card.Body>
                <div className="d-flex justify-content-between mb-2">
                  <strong>{review.user.username}</strong>
                  <div>
                    {Array(review.rating)
                      .fill()
                      .map((_, i) => (i < 5 ? <span key={i}>⭐</span> : null))}
                  </div>
                </div>
                <p>{review.comment}</p>
                <small className="text-muted">
                  {new Date(review.createdAt).toLocaleDateString()}
                </small>
              </Card.Body>
            </Card>
          ))}
        </>
      )}
    </Container>
  );
}
