import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import {
  Container,
  Card,
  Form,
  Button,
  Alert,
  Row,
  Col,
} from "react-bootstrap";
import axios from "axios";

export default function Movie() {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [rating, setRating] = useState(5);
  const [comment, setComment] = useState("");

  useEffect(() => {
    console.log(id);
    const fetchData = async () => {
      try {
        const token = localStorage.getItem("token");
        const options = {
          method: "GET",
          headers: {
            accept: "application/json",
            Authorization: `Bearer ${token}`,
          },
        };

        fetch(
          `https://api.themoviedb.org/3/movie/${id}?language=en-US`,
          options
        )
          .then((res) => res.json())
          .then((res) => {
            setMovie(res);
          })
          .catch((err) => console.error(err));

        fetch(
          `https://api.themoviedb.org/3/movie/${id}/reviews?language=en-US`,
          options
        )
          .then((res) => res.json())
          .then((res) => {
            setReviews(res);
          })
          .catch((err) => console.error(err));
        //const movieRes = await axios.get(`/api/movies/${id}`);
        //  const reviewsRes = await axios.get(`/api/reviews/movie/${id}`);
        //;
        //  setReviews(reviewsRes.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchData();
  }, [id]);

  const submitReview = async () => {
    try {
      const token = localStorage.getItem("token");
      await axios.post(
        "/api/reviews",
        {
          movie: id,
          rating,
          comment,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      alert("Review submitted successfully!");
    } catch (err) {
      alert("Error submitting review");
    }
  };

  return (
    <Container className="py-4">
      {movie && (
        <>
          <Row className="mb-4">
            <Col md={4}>
              <img
                src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`}
                alt={movie.title}
                className="img-fluid rounded shadow"
              />
            </Col>
            <Col md={8}>
              <h1>{movie.title}</h1>
              <p className="lead">{movie.overview}</p>
              <div className="mb-3">
                <span className="fw-bold">Release Date:</span>{" "}
                {movie.release_date}
              </div>
            </Col>
          </Row>

          <Card className="mb-4">
            <Card.Body>
              <Card.Title>Write a Review</Card.Title>
              <Form>
                <Form.Group className="mb-3">
                  <Form.Label>Rating</Form.Label>
                  <Form.Select
                    value={rating}
                    onChange={(e) => setRating(e.target.value)}
                  >
                    {[5, 4, 3, 2, 1].map((num) => (
                      <option key={num} value={num}>
                        {num} Stars
                      </option>
                    ))}
                  </Form.Select>
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Control
                    as="textarea"
                    rows={3}
                    placeholder="Your review..."
                    value={comment}
                    onChange={(e) => setComment(e.target.value)}
                  />
                </Form.Group>
                <Button variant="primary" onClick={submitReview}>
                  Submit Review
                </Button>
              </Form>
            </Card.Body>
          </Card>

          <h3 className="mb-3">User Reviews</h3>
          {reviews.map((review) => (
            <Card key={review._id} className="mb-3">
              <Card.Body>
                <div className="d-flex justify-content-between mb-2">
                  <strong>{review.user.username}</strong>
                  <div>
                    {Array(review.rating)
                      .fill()
                      .map((_, i) => (
                        <span key={i}>⭐</span>
                      ))}
                  </div>
                </div>
                <p>{review.comment}</p>
              </Card.Body>
            </Card>
          ))}
        </>
      )}
    </Container>
  );
}
