import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import {
  Container,
  Card,
  Form,
  Button,
  Row,
  Col,
  Badge,
} from "react-bootstrap";
import axios from "axios";

export default function Movie() {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [rating, setRating] = useState(5);
  const [comment, setComment] = useState("");
  const [userId, setUserId] = useState(null);
  const [watchListIds, setWatchlistIds] = useState([]); // Fixed typo in variable name

  const sendWatchlist = async (movieId, isAddWishlist) => {
    try {
      const token = localStorage.getItem("tokenMovie");
      const userId = JSON.parse(localStorage.getItem("user"))?.id;
      if (!userId) return;

      const optionsPost = {
        method: "POST",
        headers: {
          accept: "application/json",
          "content-type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          media_type: "movie",
          media_id: movieId,
          watchlist: !isAddWishlist,
        }),
      };

      const response = await fetch(
        `https://api.themoviedb.org/3/account/${userId}/watchlist`,
        optionsPost
      );
      const data = await response.json();

      let currentUserWatchList = JSON.parse(
        localStorage.getItem(`user_watchList_${userId}`) || "[]"
      );

      if (!isAddWishlist) {
        currentUserWatchList = [
          ...currentUserWatchList,
          { userid: userId, id: movieId },
        ];
      } else {
        currentUserWatchList = currentUserWatchList.filter(
          (obj) => !(obj.userid === userId && obj.id === movieId)
        );
      }

      localStorage.setItem(
        `user_watchList_${userId}`,
        JSON.stringify(currentUserWatchList)
      );
      setWatchlistIds(currentUserWatchList.map((item) => item.id));
      fetchData();
    } catch (err) {
      console.error("Watchlist error:", err);
      fetchData();
    }
  };

  const fetchData = async () => {
    try {
      let token = localStorage.getItem("tokenMovie");
      if (!token) {
        const response = await axios.post("/api/auth/reqToken");
        localStorage.setItem("tokenMovie", response.data.token);
        token = response.data.token;
      }

      const options = {
        method: "GET",
        headers: { Authorization: `Bearer ${token}` },
      };
      const userIdVal = JSON.parse(localStorage.getItem("user"))?.id;
      setUserId(userIdVal);

      // Fetch watchlist IDs
      if (userIdVal) {
        const userWatchList = JSON.parse(
          localStorage.getItem(`user_watchList_${userIdVal}`) || "[]"
        );
        const watchlistIds = userWatchList
          .filter((item) => item.userid === userIdVal)
          .map((item) => item.id);
        setWatchlistIds(watchlistIds);
      }

      // Fetch movie and reviews
      const movieResponse = await fetch(
        `https://api.themoviedb.org/3/movie/${id}?language=en-US`,
        options
      );
      setMovie(await movieResponse.json());

      const reviewsResponse = await fetch(
        `https://api.themoviedb.org/3/movie/${id}/reviews?language=en-US`,
        options
      );
      setReviews((await reviewsResponse.json()).results || []);
    } catch (err) {
      console.error("Fetch error:", err);
    }
  };

  useEffect(() => {
    fetchData();
  }, [id]);
  // check submit
  const submitReview = async () => {
    try {
      if (!userId) {
        alert("Please login to submit a review");
        return;
      }
      const token = localStorage.getItem("token");
      console.log(rating);
      console.log(comment);
      let movie = {
        userid: userId,
        id: id,
        review: comment,
        rating: rating,
      };
      let currentRatedMovies = [];
      if (localStorage.getItem("reviewed_movies")) {
        currentRatedMovies = JSON.parse(
          localStorage.getItem("reviewed_movies")
        );
      }
      currentRatedMovies.push(movie);
      localStorage.setItem(
        "reviewed_movies",
        JSON.stringify(currentRatedMovies)
      );
      alert("Review submitted successfully!");
    } catch (err) {
      alert("Error submitting review");
    }
  };

  return (
    <Container className="py-5">
      {movie && (
        <>
          <Row className="mb-5">
            <Col md={4}>
              <div className="mb-4 position-relative">
                <img
                  src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`}
                  alt={movie.title}
                  className="img-fluid rounded shadow-lg"
                  style={{ width: "100%" }}
                />
                {movie.vote_average && (
                  <div
                    className="position-absolute d-flex align-items-center justify-content-center rounded-circle bg-dark text-white"
                    style={{
                      width: "60px",
                      height: "60px",
                      right: "15px",
                      top: "15px",
                      opacity: "0.85",
                      border: "2px solid gold",
                    }}
                  >
                    <span className="fw-bold">
                      {movie.vote_average.toFixed(1)}
                    </span>
                  </div>
                )}
              </div>
              <div className="mb-4">
                {userId && (
                  <button
                    onClick={() =>
                      sendWatchlist(movie.id, watchListIds.includes(movie.id))
                    }
                    className={
                      watchListIds.includes(movie.id) // Fixed variable name
                        ? "btn btn-warning w-100 py-2 fw-bold shadow"
                        : "btn btn-primary w-100 py-2 fw-bold shadow"
                    }
                  >
                    {watchListIds.includes(movie.id) ? ( // Fixed variable name
                      <>
                        <i className="bi bi-bookmark-check-fill me-2"></i>Remove
                        From Watchlist
                      </>
                    ) : (
                      <>
                        <i className="bi bi-bookmark-plus me-2"></i>Add To
                        Watchlist
                      </>
                    )}
                  </button>
                )}
              </div>
            </Col>
            <Col md={8}>
              <h1 className="display-4 fw-bold mb-3">{movie.title}</h1>
              {movie.tagline && (
                <p className="text-muted fst-italic mb-3">"{movie.tagline}"</p>
              )}
              <div className="mb-4">
                <Badge bg="secondary" className="me-2">
                  {movie.release_date}
                </Badge>
                {movie.runtime && (
                  <Badge bg="secondary" className="me-2">
                    {Math.floor(movie.runtime / 60)}h {movie.runtime % 60}m
                  </Badge>
                )}
                {movie.vote_average && (
                  <Badge bg="warning" text="dark" className="me-2">
                    ★ {movie.vote_average.toFixed(1)}/10
                  </Badge>
                )}
              </div>
              <p className="lead border-start border-4 border-primary ps-3 mb-4">
                {movie.overview}
              </p>

              {movie.genres && (
                <div className="mb-4">
                  <h5 className="text-secondary mb-2">Genres</h5>
                  <div>
                    {movie.genres.map((genre) => (
                      <Badge
                        key={genre.id}
                        bg="primary"
                        className="me-2 mb-2 py-2 px-3"
                      >
                        {genre.name}
                      </Badge>
                    ))}
                  </div>
                </div>
              )}

              <div className="mb-4">
                <Card className="shadow border-0">
                  <Card.Body className="p-4">
                    <Card.Title className="border-bottom pb-3 mb-3">
                      <i className="bi bi-star me-2"></i>
                      Write a Review
                    </Card.Title>
                    <Form>
                      <Form.Group className="mb-3">
                        <Form.Label className="fw-bold">Your Rating</Form.Label>
                        <Form.Select
                          value={rating}
                          onChange={(e) => setRating(e.target.value)}
                          className="py-2"
                        >
                          {[5, 4, 3, 2, 1].map((num) => (
                            <option key={num} value={num}>
                              {num} {num === 1 ? "Star" : "Stars"}
                            </option>
                          ))}
                        </Form.Select>
                      </Form.Group>
                      <Form.Group className="mb-3">
                        <Form.Label className="fw-bold">
                          Your Comment
                        </Form.Label>
                        <Form.Control
                          as="textarea"
                          rows={4}
                          placeholder="Share your thoughts about this movie..."
                          value={comment}
                          onChange={(e) => setComment(e.target.value)}
                          className="py-2"
                        />
                      </Form.Group>
                      <Button
                        variant="primary"
                        onClick={submitReview}
                        className="py-2 px-4 fw-bold"
                      >
                        Submit Review
                      </Button>
                    </Form>
                  </Card.Body>
                </Card>
              </div>
            </Col>
          </Row>

          <div className="mb-5">
            <h3 className="mb-4 border-bottom pb-2">
              <i className="bi bi-chat-quote me-2"></i>
              User Reviews {reviews.length > 0 && `(${reviews.length})`}
            </h3>

            {reviews.length > 0 ? (
              reviews.map((review) => (
                <Card key={review.id} className="mb-3 shadow-sm border-0">
                  <Card.Body className="p-4">
                    <div className="d-flex justify-content-between align-items-center mb-3">
                      <div className="d-flex align-items-center">
                        {review.author_details.avatar_path ? (
                          <img
                            src={`https://image.tmdb.org/t/p/w45${review.author_details.avatar_path}`}
                            alt={review.author}
                            className="rounded-circle me-2"
                            style={{ width: "40px", height: "40px" }}
                          />
                        ) : (
                          <div
                            className="rounded-circle bg-secondary text-white d-flex align-items-center justify-content-center me-2"
                            style={{ width: "40px", height: "40px" }}
                          >
                            {review.author.charAt(0).toUpperCase()}
                          </div>
                        )}
                        <div>
                          <strong className="d-block">{review.author}</strong>
                          <small className="text-muted">
                            {new Date(review.created_at).toLocaleDateString()}
                          </small>
                        </div>
                      </div>
                      <div>
                        {review.author_details.rating && (
                          <span className="badge">
                            {Array(Math.min(review.author_details.rating, 5))
                              .fill()
                              .map((_, i) => (
                                <span key={i}>⭐</span>
                              ))}
                          </span>
                        )}
                      </div>
                    </div>
                    <p className="mb-0">{review.content}</p>
                  </Card.Body>
                </Card>
              ))
            ) : (
              <div className="text-center py-5 bg-light rounded">
                <p className="text-muted mb-0">
                  No reviews yet. Be the first to review!
                </p>
              </div>
            )}
          </div>
        </>
      )}

      {!movie && (
        <div className="text-center py-5">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-3 text-muted">Loading movie details...</p>
        </div>
      )}
    </Container>
  );
}
