import { useState, useEffect } from "react";
import { Form, Row, Col, Card, Spinner } from "react-bootstrap";
import { Link } from "react-router-dom";

export default function Search() {
  const [query, setQuery] = useState("");
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(false);
  const apiKey = "341cf1f6a54f15a1c916c3a4adf58076";
  console.log(apiKey);
  useEffect(() => {
    const searchMovies = async () => {
      setLoading(true);
      let queryStr = "";
      if (query.length > 0) {
        queryStr = `/search/movie?query="${query}"&include_adult=false&language=en-US&page=1`; // add page query
      } else {
        queryStr =
          "/discover/movie?include_adult=false&include_video=false&language=en-US&page=1&sort_by=popularity.desc"; // add page query
      }
      try {
        // gawin mong dynamic yung bearer token
        const token = localStorage.getItem("token");
        const options = {
          method: "GET",
          headers: {
            accept: "application/json",
            Authorization: `Bearer ${token}`,
          },
        };

        fetch("https://api.themoviedb.org/3" + queryStr, options)
          .then((res) => res.json())
          .then((res) => {
            console.log(res.results);
            setMovies(res.results);
          })
          .catch((err) => console.error(err));

        // const res = await axios.get(`/api/movies/search`);
        // console.log(res);

        // const response = await axios.get(`/api/movies/search?query=${query}`);
        // console.log(response);
        // setMovies(response.data);
      } catch (err) {
        console.error(err);
      }
      setLoading(false);
    };

    const timer = setTimeout(searchMovies, 500);
    return () => clearTimeout(timer);
  }, [query]);

  return (
    <div className="p-4">
      <Form className="mb-4">
        <Form.Control
          size="lg"
          type="search"
          placeholder="Search for movies..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
      </Form>

      {loading && <Spinner animation="border" className="d-block mx-auto" />}

      <Row xs={1} md={4} className="g-4">
        {movies.map((movie) => (
          <Col key={movie.id}>
            <Card className="h-100 shadow-sm">
              <Card.Img
                variant="top"
                src={
                  movie.poster_path
                    ? `https://image.tmdb.org/t/p/w500${movie.poster_path}`
                    : "/placeholder.jpg"
                }
              />
              <Card.Body>
                <Card.Title>{movie.title}</Card.Title>
                <Card.Text className="text-muted small">
                  {movie.release_date} â€¢ {movie.genres?.join(", ")}
                </Card.Text>
                <Link
                  to={`/movie/${movie.id}`}
                  className="btn btn-primary w-100"
                >
                  View Details
                </Link>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </div>
  );
}
