import React, { useEffect, useState } from "react";
import { ListGroup, Card, Spinner, Row, Col, Alert } from "react-bootstrap";
import { Link } from "react-router-dom";
import axios from "axios";
export default function Profile() {
  const [user, setUser] = useState({});
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [watchlist, setWatchlistData] = useState([]);

  const getWatchList = async () => {
    setLoading(true);
    try {
      let token = localStorage.getItem("tokenMovie") || null;
      if (!token) {
        const response = await axios.post("/api/auth/reqToken");
        console.log("Token response:", response.data);
        localStorage.setItem("tokenMovie", response.data.token);
        token = response.data.token;
      }
      const options = {
        method: "GET",
        headers: {
          accept: "application/json",
          Authorization: `Bearer ${token}`,
        },
      };
      const userId = JSON.parse(localStorage.getItem("user")).id;

      console.log("userId", userId);

      fetch(
        `https://api.themoviedb.org/3/account/${userId}/watchlist/movies?language=en-US&page=1&sort_by=created_at.asc`,
        options
      )
        .then((res) => res.json())
        .then((res) => {
          console.log(res);
          setUser(JSON.parse(localStorage.getItem("user")));
          getMovieGenre(options, res);
          let reviewedMovies = [];
          let movie1 = { id: 939243, review: "good 1", rating: 5 };
          let movie2 = { id: 1084199, review: "good 2", rating: 2 };
          let movie3 = { id: 1064213, review: "good 3", rating: 4 };

          reviewedMovies.push(movie1);
          reviewedMovies.push(movie2);
          reviewedMovies.push(movie3);
          localStorage.setItem(
            "reviewed_movies",
            JSON.stringify(reviewedMovies)
          );

          if (localStorage.getItem("reviewed_movies")) {
            fetchReviewedMovie(
              JSON.parse(localStorage.getItem("reviewed_movies")),
              options
            );
          }
          setLoading(false);
        })
        .catch((err) => console.error(err));
    } catch (err) {
      console.error("Search error:", err);
      setWatchlistData([]);
    } finally {
      setLoading(false);
    }
  };

  const getMovieGenre = async (options, moviesList) => {
    try {
      fetch(
        "https://api.themoviedb.org/3/genre/movie/list?language=en",
        options
      )
        .then((res) => res.json())
        .then((resGenre) => {
          // map res.results to check genre
          moviesList.results.map((movie) => {
            let genresMovie = [];
            movie.genre_ids.map((genreId) => {
              resGenre.genres.map((genreItem) => {
                if (genreItem.id === genreId) {
                  genresMovie.push(genreItem.name);
                }
              });
            });
            movie.genres = genresMovie;
          });
          setWatchlistData(moviesList.results || []);
        })
        .catch((err) => console.error(err));
    } catch (err) {
      console.error(err);
    }
  };

  const removeWatchList = async (movieId) => {
    try {
      const token = localStorage.getItem("tokenMovie");
      const userId = JSON.parse(localStorage.getItem("user")).id;
      console.log("userId", userId);

      const optionsPost = {
        method: "POST",
        headers: {
          accept: "application/json",
          "content-type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          media_type: "movie",
          media_id: movieId,
          watchlist: false,
        }),
      };

      fetch(
        `https://api.themoviedb.org/3/account/${userId}/watchlist`,
        optionsPost
      )
        .then((res) => res.json())
        .then((res) => {
          getWatchList();
        })
        .catch((err) => {
          console.log(err);
          getWatchList();
        });
    } catch (err) {
      console.error(err);
    }
  };

  const fetchReviewedMovie = async (reviewedMovies, options) => {
    let movieData = [];
    await reviewedMovies.map(async (movie) => {
      await fetch(
        `https://api.themoviedb.org/3/movie/${movie.id}?language=en-US`,
        options
      )
        .then((res) => res.json())
        .then((res) => {
          res.review = movie.review;
          res.rating = movie.rating;
          res.comment = movie.review;
          movieData.push(res);
          console.log("reviewwww", res);
        })

        .catch((err) => console.error(err));
    });
    setReviews(movieData);
  };
  useEffect(() => {
    getWatchList();
  }, []);

  if (loading)
    return <Spinner animation="border" className="d-block mx-auto mt-5" />;

  return (
    <div className="container mt-4">
      {error && <Alert variant="danger">{error}</Alert>}

      <Card className="mb-4">
        <Card.Body>
          <Card.Title style={{ textTransform: "capitalize" }}>
            {user.username}
          </Card.Title>
          <Card.Text>Email: {user.email}</Card.Text>
          <Card.Text>Watchlist Card: {watchlist.length}</Card.Text>
        </Card.Body>
      </Card>

      <h3>My Reviews</h3>
      {reviews.length === 0 ? (
        <p className="text-muted">No reviews yet</p>
      ) : (
        <ListGroup className="mb-4">
          {reviews.map((review) => (
            <ListGroup.Item key={review.title}>
              <div className="d-flex justify-content-between align-items-start">
                <div>
                <Card className="h-100">
                  <Card.Img
                    variant="top"
                    src={
                      review.poster_path
                        ? `https://image.tmdb.org/t/p/w500${review.poster_path}`
                        : "/placeholder.jpg"
                    }
                  />
                  <h5>
                    <Link to={`/movie/${review.id}`}>{review.title}</Link>
                  </h5>
                  {review.genres && (
                    <div>
                      <span className="fw-bold">Genres:</span>{" "}
                      {review.genres.map((genre) => genre.name).join(", ")}
                    </div>
                  )}
                  <div className="text-warning">
                    {"★".repeat(review.rating)}
                    {"☆".repeat(5 - review.rating)}
                  </div>
                  <p>{review.comment}</p>
                </Card>
                </div>
              </div>
            </ListGroup.Item>
          ))}
        </ListGroup>
      )}

      <h3>My Watchlist</h3>
      {watchlist.length === 0 ? (
        <p className="text-muted">Watchlist is empty</p>
      ) : (
        <Row xs={1} md={4} className="g-4">
          {watchlist.map((movie) => (
            <Col key={movie.id}>
              <Card className="h-100">
                <Card.Img
                  variant="top"
                  src={
                    movie.poster_path
                      ? `https://image.tmdb.org/t/p/w500${movie.poster_path}`
                      : "/placeholder.jpg"
                  }
                />
                <Card.Body>
                  <Card.Title>{movie.title}</Card.Title>
                  <Card.Text className="text-muted small">
                    {movie.release_date &&
                      new Date(movie.release_date).getFullYear()}
                    {movie.vote_average &&
                      ` • ⭐ ${
                        movie.vote_average > 5
                          ? 5
                          : movie.vote_average.toFixed(1)
                      }`}
                  </Card.Text>
                  <Card.Text>
                    {movie.genres && movie.genres.join(", ")}
                  </Card.Text>
                  <Link
                    to={`/movie/${movie.id}`}
                    className="btn btn-primary w-100"
                  >
                    View Details
                  </Link>
                  <button
                    onClick={() => removeWatchList(movie.id)}
                    className="btn btn-warning w-100 mt-2"
                  >
                    Remove To Watchlist
                  </button>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      )}
    </div>
  );
}
