import React, { useEffect, useState } from "react";
import { ListGroup, Card, Spinner, Row, Col, Alert } from "react-bootstrap";
import { Link } from "react-router-dom";
import axios from "axios";
import { useAuth } from "../context/AuthContext";

export default function Profile() {
  const { user } = useAuth();
  const [reviews, setReviews] = useState([]);
  const [watchlist, setWatchlist] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const getWatchList = async () => {
    setLoading(true);
    try {
      let token = localStorage.getItem("tokenMovie") || null;
      if (!token) {
        const response = await axios.post("/api/auth/reqToken");
        console.log("Token response:", response.data);
        localStorage.setItem("tokenMovie", response.data.token);
        token = response.data.token;
      }
      const options = {
        method: "GET",
        headers: {
          accept: "application/json",
          Authorization: `Bearer ${token}`,
        },
      };
      const userId = JSON.parse(localStorage.getItem("user")).id;
      console.log("userId", userId);

      fetch(
        `https://api.themoviedb.org/3/account/${userId}/watchlist/movies?language=en-US&page=1&sort_by=created_at.asc`,
        options
      )
        .then((res) => res.json())
        .then((res) => {
          console.log(res);
          setWatchlistData(res.results || []);
        })
        .catch((err) => console.error(err));
    } catch (err) {
      console.error("Search error:", err);
      setWatchlistData([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const loadData = async () => {
      try {
        const [reviewsRes, watchRes] = await Promise.all([
          axios.get(`/api/reviews/user/${user.id}`),
          axios.get(`/api/users/me/watchlist`, {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
          }),
        ]);

        setReviews(reviewsRes.data);
        setWatchlist(watchRes.data);
      } catch (err) {
        setError("Failed to load profile data");
      } finally {
        setLoading(false);
      }
    };
    loadData();
  }, [user]);

  if (loading)
    return <Spinner animation="border" className="d-block mx-auto mt-5" />;

  return (
    <div className="container mt-4">
      {error && <Alert variant="danger">{error}</Alert>}

      <Card className="mb-4">
        <Card.Body>
          <Card.Title>{user.username}</Card.Title>
          <Card.Text>
            Member since: {new Date(user.createdAt).toLocaleDateString()}
          </Card.Text>
        </Card.Body>
      </Card>

      <h3>My Reviews</h3>
      {reviews.length === 0 ? (
        <p className="text-muted">No reviews yet</p>
      ) : (
        <ListGroup className="mb-4">
          {reviews.map((review) => (
            <ListGroup.Item key={review._id}>
              <div className="d-flex justify-content-between align-items-start">
                <div>
                  <h5>
                    <Link to={`/movie/${review.movie._id}`}>
                      {review.movie.title}
                    </Link>
                  </h5>
                  <div className="text-warning">
                    {"★".repeat(review.rating)}
                    {"☆".repeat(5 - review.rating)}
                  </div>
                  <p>{review.comment}</p>
                </div>
                <small className="text-muted">
                  {new Date(review.createdAt).toLocaleDateString()}
                </small>
              </div>
            </ListGroup.Item>
          ))}
        </ListGroup>
      )}

      <h3>My Watchlist</h3>
      {watchlist.length === 0 ? (
        <p className="text-muted">Watchlist is empty</p>
      ) : (
        <Row xs={1} md={4} className="g-4">
          {watchlist.map((movie) => (
            <Col key={movie._id}>
              <Card className="h-100">
                <Card.Img
                  variant="top"
                  src={`https://image.tmdb.org/t/p/w500${movie.posterPath}`}
                />
                <Card.Body>
                  <Card.Title>{movie.title}</Card.Title>
                  <Card.Text className="text-muted small">
                    {new Date(movie.releaseDate).getFullYear()}
                  </Card.Text>
                  <Link
                    to={`/movie/${movie._id}`}
                    className="btn btn-primary w-100"
                  >
                    View Details
                  </Link>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      )}
    </div>
  );
}
