import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { Container, Card, Form, Button, Row, Col, Alert } from "react-bootstrap";
import axios from "axios";
import { useAuth } from "../context/AuthContext";

export default function Movie() {
  const { id } = useParams();
  const { user } = useAuth();
  const [movie, setMovie] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [rating, setRating] = useState(5);
  const [comment, setComment] = useState("");
  const [inWatchlist, setInWatchlist] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        setError("");

        // Fetch movie details from TMDB
        const movieResponse = await axios.get(
          `https://api.themoviedb.org/3/movie/${id}`,
          {
            params: {
              api_key: process.env.REACT_APP_TMDB_API_KEY,
              append_to_response: "release_dates,credits"
            }
          }
        );

        // Fetch reviews from TMDB
        const reviewsResponse = await axios.get(
          `https://api.themoviedb.org/3/movie/${id}/reviews`,
          {
            params: {
              api_key: process.env.REACT_APP_TMDB_API_KEY
            }
          }
        );

        // Check if movie is in watchlist
        if (user) {
          const watchlistCheck = await axios.get(`/api/users/watchlist/check/${id}`, {
            headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
          });
          setInWatchlist(watchlistCheck.data.inWatchlist);
        }

        setMovie(movieResponse.data);
        setReviews(reviewsResponse.data.results || []);
      } catch (err) {
        setError("Failed to load movie data");
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [id, user]);

  const handleWatchlist = async () => {
    if (!user) {
      alert("Please login to manage your watchlist");
      return;
    }

    try {
      const endpoint = inWatchlist ? "remove" : "add";
      await axios.post(`/api/users/watchlist/${endpoint}/${id}`, null, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
      });
      setInWatchlist(!inWatchlist);
    } catch (err) {
      alert("Error updating watchlist");
    }
  };

  const submitReview = async () => {
    if (!user) {
      alert("Please login to submit a review");
      return;
    }

    try {
      await axios.post(
        "/api/reviews",
        {
          movie: id,
          rating,
          comment,
        },
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      );
      alert("Review submitted successfully!");
      setComment("");
      setRating(5);
    } catch (err) {
      alert("Error submitting review");
    }
  };

  if (loading) return <div className="text-center mt-4">Loading...</div>;
  if (error) return <Alert variant="danger" className="mt-4">{error}</Alert>;

  return (
    <Container className="py-4">
      <Row className="mb-4">
        <Col md={4}>
          <img
            src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`}
            alt={movie.title}
            className="img-fluid rounded shadow"
          />
          {user && (
            <Button 
              variant={inWatchlist ? "secondary" : "primary"}
              className="w-100 mt-3"
              onClick={handleWatchlist}
            >
              {inWatchlist ? 'âœ“ In Watchlist' : '+ Add to Watchlist'}
            </Button>
          )}
        </Col>
        
        <Col md={8}>
          <div className="movie-metadata">
            <h1>{movie.title} ({new Date(movie.release_date).getFullYear()})</h1>
            
            <div className="rating-section mb-3">
              <span className="rating-number h4 me-3">
                {movie.vote_average.toFixed(1)}/10
              </span>
              <div className="star-rating d-inline-block">
                {[...Array(5)].map((_, i) => (
                  <span 
                    key={i} 
                    className={`star fs-4 ${i < Math.floor(movie.vote_average / 2) ? 'text-warning' : 'text-secondary'}`}
                  >
                    â˜…
                  </span>
                ))}
              </div>
            </div>

            <p className="genres mb-4">
              <strong>Genres:</strong>{" "}
              {movie.genres.map(g => g.name).join(", ")}
            </p>

            <h4>Overview</h4>
            <p className="lead">{movie.overview}</p>

            <div className="movie-details mt-4">
              <Row>
                <Col md={6}>
                  <p><strong>Release Date:</strong> {movie.release_date}</p>
                  <p><strong>Runtime:</strong> {movie.runtime} minutes</p>
                </Col>
                <Col md={6}>
                  <p><strong>Budget:</strong> ${movie.budget?.toLocaleString() || "N/A"}</p>
                  <p><strong>Revenue:</strong> ${movie.revenue?.toLocaleString() || "N/A"}</p>
                </Col>
              </Row>
            </div>
          </div>
        </Col>
      </Row>

      {user && (
        <Card className="mb-4">
          <Card.Body>
            <Card.Title>Write a Review</Card.Title>
            <Form>
              <Form.Group className="mb-3">
                <Form.Label>Rating</Form.Label>
                <Form.Select
                  value={rating}
                  onChange={(e) => setRating(Number(e.target.value))}
                >
                  {[5, 4, 3, 2, 1].map((num) => (
                    <option key={num} value={num}>
                      {num} Stars
                    </option>
                  ))}
                </Form.Select>
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Control
                  as="textarea"
                  rows={3}
                  placeholder="Your review (max 500 characters)..."
                  value={comment}
                  onChange={(e) => setComment(e.target.value)}
                  maxLength={500}
                />
                <Form.Text className="text-muted">
                  {500 - comment.length} characters remaining
                </Form.Text>
              </Form.Group>
              <Button variant="primary" onClick={submitReview}>
                Submit Review
              </Button>
            </Form>
          </Card.Body>
        </Card>
      )}

      <h3 className="mb-3">User Reviews</h3>
      {reviews.length === 0 ? (
        <Alert variant="info">No reviews yet. Be the first to write one!</Alert>
      ) : (
        reviews.map((review) => (
          <Card key={review.id} className="mb-3">
            <Card.Body>
              <div className="d-flex justify-content-between mb-2">
                <strong>{review.author}</strong>
                <div className="text-warning">
                  {"â˜…".repeat(review.author_details.rating)}
                  {"â˜†".repeat(5 - (review.author_details.rating || 0))}
                </div>
              </div>
              <p>{review.content}</p>
              <small className="text-muted">
                Posted on {new Date(review.created_at).toLocaleDateString()}
              </small>
            </Card.Body>
          </Card>
        ))
      )}
    </Container>
  );
}
