import React, { useEffect, useState } from "react";
import { ListGroup, Card, Spinner, Row, Col, Alert, Badge } from "react-bootstrap";
import { Link } from "react-router-dom";
import axios from "axios";

export default function Profile() {
  const [user, setUser] = useState({});
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [watchlist, setWatchlistData] = useState([]);

  const getWatchList = async () => {
    setLoading(true);
    try {
      let token = localStorage.getItem("tokenMovie") || null;
      if (!token) {
        const response = await axios.post("/api/auth/reqToken");
        console.log("Token response:", response.data);
        localStorage.setItem("tokenMovie", response.data.token);
        token = response.data.token;
      }
      const options = {
        method: "GET",
        headers: {
          accept: "application/json",
          Authorization: `Bearer ${token}`,
        },
      };
      const userId = JSON.parse(localStorage.getItem("user")).id;

      console.log("userId", userId);

      fetch(
        `https://api.themoviedb.org/3/account/${userId}/watchlist/movies?language=en-US&page=1&sort_by=created_at.asc`,
        options
      )
        .then((res) => res.json())
        .then((res) => {
          console.log(res);
          setUser(JSON.parse(localStorage.getItem("user")));
          getMovieGenre(options, res);
          if (localStorage.getItem("reviewed_movies")) {
            fetchReviewedMovie(
              JSON.parse(localStorage.getItem("reviewed_movies")),
              options,
              userId
            );
          }
          setLoading(false);
        })
        .catch((err) => console.error(err));
    } catch (err) {
      console.error("Search error:", err);
      setWatchlistData([]);
    } finally {
      setLoading(false);
    }
  };

  const getMovieGenre = async (options, moviesList) => {
    try {
      fetch(
        "https://api.themoviedb.org/3/genre/movie/list?language=en",
        options
      )
        .then((res) => res.json())
        .then((resGenre) => {
          // map res.results to check genre
          moviesList.results.map((movie) => {
            let genresMovie = [];
            movie.genre_ids.map((genreId) => {
              resGenre.genres.map((genreItem) => {
                if (genreItem.id === genreId) {
                  genresMovie.push(genreItem.name);
                }
              });
            });
            movie.genres = genresMovie;
          });
          setWatchlistData(moviesList.results || []);
        })
        .catch((err) => console.error(err));
    } catch (err) {
      console.error(err);
    }
  };

  const removeWatchList = async (movieId) => {
    try {
      const token = localStorage.getItem("tokenMovie");
      const userId = JSON.parse(localStorage.getItem("user")).id;
      console.log("userId", userId);

      const optionsPost = {
        method: "POST",
        headers: {
          accept: "application/json",
          "content-type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          media_type: "movie",
          media_id: movieId,
          watchlist: false,
        }),
      };

      fetch(
        `https://api.themoviedb.org/3/account/${userId}/watchlist`,
        optionsPost
      )
        .then((res) => res.json())
        .then((res) => {
          getWatchList();
        })
        .catch((err) => {
          console.log(err);
          getWatchList();
        });
    } catch (err) {
      console.error(err);
    }
  };

  const fetchReviewedMovie = async (reviewedMovies, options, userId) => {
    let movieData = [];
    await reviewedMovies.map(async (movie) => {
      if (movie.userid === userId) {
        await fetch(
          `https://api.themoviedb.org/3/movie/${movie.id}?language=en-US`,
          options
        )
          .then((res) => res.json())
          .then((res) => {
            res.review = movie.review;
            res.rating = movie.rating;
            res.comment = movie.review;
            movieData.push(res);
            console.log("reviewwww", res);
          })
          .catch((err) => console.error(err));
      }
    });
    setReviews(movieData);
  };
  useEffect(() => {
    getWatchList();
  }, []);

  if (loading)
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ height: "80vh" }}>
        <Spinner animation="border" variant="primary" style={{ width: "3rem", height: "3rem" }} />
      </div>
    );

  return (
    <div className="container mt-5 mb-5">
      {error && <Alert variant="danger" className="shadow-sm">{error}</Alert>}

      <Card className="mb-5 shadow border-0">
        <Card.Body className="p-4">
          <div className="d-flex align-items-center mb-3">
            <div className="bg-primary text-white rounded-circle d-flex justify-content-center align-items-center me-3" 
                 style={{ width: "50px", height: "50px", fontSize: "1.5rem" }}>
              {user.fullName ? user.fullName.charAt(0).toUpperCase() : "U"}
            </div>
            <div>
              <Card.Title as="h3" style={{ textTransform: "capitalize", marginBottom: "0.2rem" }}>
                {user.fullName}
              </Card.Title>
              <span className="text-muted">@{user.username}</span>
            </div>
          </div>
          <hr />
          <div className="row">
            <div className="col-md-6">
              <Card.Text><i className="bi bi-envelope me-2"></i> {user.email}</Card.Text>
            </div>
            <div className="col-md-6">
              <Card.Text>
                <span className="d-flex align-items-center">
                  <i className="bi bi-film me-2"></i>
                  Watchlist: <Badge bg="primary" pill className="ms-2">{watchlist.length}</Badge>
                </span>
              </Card.Text>
            </div>
          </div>
        </Card.Body>
      </Card>

      <div className="mb-4">
        <div className="d-flex align-items-center mb-3">
          <h3 className="mb-0 me-2">My Reviews</h3>
          <Badge bg="secondary" pill>{reviews.length}</Badge>
        </div>
        
        {reviews.length === 0 ? (
          <Card className="text-center p-4 border-0 shadow-sm">
            <p className="text-muted mb-0">You haven't written any reviews yet</p>
          </Card>
        ) : (
          <ListGroup className="mb-4 shadow-sm">
            {reviews.map((review) => (
              <ListGroup.Item key={review.title} className="p-3 border-0 mb-2">
                <div className="d-flex">
                  <div className="me-3" style={{ width: "100px" }}>
                    <Card.Img
                      className="rounded shadow-sm"
                      src={
                        review.poster_path
                          ? `https://image.tmdb.org/t/p/w500${review.poster_path}`
                          : "/placeholder.jpg"
                      }
                      alt={review.title}
                    />
                  </div>
                  <div className="flex-grow-1">
                    <h5 className="mb-1">
                      <Link to={`/movie/${review.id}`} className="text-decoration-none">{review.title}</Link>
                    </h5>
                    {review.genres && (
                      <div className="mb-2">
                        {review.genres.map((genre) => (
                          <Badge bg="secondary" className="me-1" key={genre.name}>{genre.name}</Badge>
                        ))}
                      </div>
                    )}
                    <div className="text-warning mb-2 fs-5">
                      {"★".repeat(review.rating)}
                      {"☆".repeat(5 - review.rating)}
                    </div>
                    <p className="mb-0 text-muted">{review.comment}</p>
                  </div>
                </div>
              </ListGroup.Item>
            ))}
          </ListGroup>
        )}
      </div>

      <div>
        <div className="d-flex align-items-center mb-3">
          <h3 className="mb-0 me-2">My Watchlist</h3>
          <Badge bg="primary" pill>{watchlist.length}</Badge>
        </div>
        
        {watchlist.length === 0 ? (
          <Card className="text-center p-4 border-0 shadow-sm">
            <p className="text-muted mb-0">Your watchlist is empty</p>
          </Card>
        ) : (
          <Row xs={1} md={2} lg={4} className="g-4">
            {watchlist.map((movie) => (
              <Col key={movie.id}>
                <Card className="h-100 shadow-sm border-0 hover-card">
                  <div className="position-relative">
                    <Card.Img
                      variant="top"
                      src={
                        movie.poster_path
                          ? `https://image.tmdb.org/t/p/w500${movie.poster_path}`
                          : "/placeholder.jpg"
                      }
                      className="movie-poster"
                      alt={movie.title}
                    />
                    {movie.vote_average && (
                      <span className="position-absolute top-0 end-0 bg-primary text-white px-2 py-1 m-2 rounded-pill">
                        ⭐ {movie.vote_average > 5 ? 5 : movie.vote_average.toFixed(1)}
                      </span>
                    )}
                  </div>
                  <Card.Body>
                    <Card.Title className="text-truncate fw-bold">{movie.title}</Card.Title>
                    <Card.Text className="text-muted small mb-2">
                      {movie.release_date && new Date(movie.release_date).getFullYear()}
                    </Card.Text>
                    <Card.Text className="mb-3">
                      {movie.genres && movie.genres.map((genre, index) => (
                        <span key={index} className="badge bg-light text-dark me-1 mb-1">{genre}</span>
                      ))}
                    </Card.Text>
                    <div className="d-grid gap-2">
                      <Link
                        to={`/movie/${movie.id}`}
                        className="btn btn-primary"
                      >
                        View Details
                      </Link>
                      <button
                        onClick={() => removeWatchList(movie.id)}
                        className="btn btn-outline-danger"
                      >
                        Remove from Watchlist
                      </button>
                    </div>
                  </Card.Body>
                </Card>
              </Col>
            ))}
          </Row>
        )}
      </div>
    </div>
  );
}
