import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { Container, Card, Form, Button, Row, Col } from "react-bootstrap";
import axios from "axios";

export default function Movie() {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [rating, setRating] = useState(5);
  const [comment, setComment] = useState("");
  const [inWatchlist, setInWatchlist] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch movie details
        const movieResponse = await axios.get(
          `https://api.themoviedb.org/3/movie/${id}?api_key=${process.env.REACT_APP_TMDB_API_KEY}`
        );
        
        // Fetch reviews
        const reviewsResponse = await axios.get(
          `https://api.themoviedb.org/3/movie/${id}/reviews?api_key=${process.env.REACT_APP_TMDB_API_KEY}`
        );

        // Check watchlist status
        const watchlistCheck = await axios.get(`/api/users/watchlist/check/${id}`, {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
        });

        setMovie(movieResponse.data);
        setReviews(reviewsResponse.data.results || []);
        setInWatchlist(watchlistCheck.data.inWatchlist);
      } catch (err) {
        console.error(err);
      }
    };
    fetchData();
  }, [id]);

  const handleWatchlist = async () => {
    try {
      const endpoint = inWatchlist ? "remove" : "add";
      await axios.post(`/api/users/watchlist/${endpoint}/${id}`, null, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
      });
      setInWatchlist(!inWatchlist);
    } catch (err) {
      alert("Error updating watchlist");
    }
  };

  const submitReview = async () => {
    try {
      await axios.post(
        "/api/reviews",
        {
          movie: id,
          rating,
          comment,
        },
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      );
      alert("Review submitted successfully!");
    } catch (err) {
      alert("Error submitting review");
    }
  };

  if (!movie) return <div>Loading...</div>;

  return (
    <Container className="py-4">
      <Row className="mb-4">
        <Col md={4}>
          <img
            src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`}
            alt={movie.title}
            className="img-fluid rounded shadow"
          />
          <button 
            onClick={handleWatchlist}
            className={`w-100 mt-3 btn ${inWatchlist ? 'btn-secondary' : 'btn-primary'}`}
          >
            {inWatchlist ? 'In Watchlist' : 'Add to Watchlist'}
          </button>
        </Col>
        <Col md={8}>
          <div className="movie-metadata">
            <h2>{movie.title} ({new Date(movie.release_date).getFullYear()})</h2>
            <div className="rating-section mb-3">
              <span className="rating-number">
                {movie.vote_average.toFixed(1)}/10
              </span>
              <div className="star-rating">
                {[...Array(5)].map((_, i) => (
                  <span 
                    key={i} 
                    className={`star ${i < Math.floor(movie.vote_average / 2) ? 'text-warning' : 'text-secondary'}`}
                  >
                    ★
                  </span>
                ))}
              </div>
            </div>
            <p className="genres">
              {movie.genres.map(g => g.name).join(", ")}
            </p>
            <p className="lead">{movie.overview}</p>
          </div>
        </Col>
      </Row>

      {/* Rest of your existing review code remains here */}
      <Card className="mb-4">
        <Card.Body>
          <Card.Title>Write a Review</Card.Title>
          <Form>
            {/* Existing review form elements */}
          </Form>
        </Card.Body>
      </Card>

      <h3 className="mb-3">User Reviews</h3>
      {reviews.map((review) => (
        <Card key={review.id} className="mb-3">
          <Card.Body>
            {/* Existing review display */}
          </Card.Body>
        </Card>
      ))}
    </Container>
  );
}
