import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { Container, Card, Form, Button, Row, Col } from "react-bootstrap";
import axios from "axios";

export default function Movie() {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [rating, setRating] = useState(5);
  const [comment, setComment] = useState("");
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const movieRes = await axios.get(`/api/movies/${id}`);
        const reviewsRes = await axios.get(`/api/reviews/movie/${id}`);
        setMovie(movieRes.data);
        setReviews(reviewsRes.data);
      } catch (err) {
        console.error("Error fetching data:", err);
        setError("Failed to load movie data");
      }
    };
    fetchData();
  }, [id]);

  const submitReview = async () => {
    try {
      const token = localStorage.getItem("token");
      await axios.post(
        "/api/reviews",
        {
          movie: id,
          rating: Number(rating),
          comment,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      // Refresh reviews after submission
      const reviewsRes = await axios.get(`/api/reviews/movie/${id}`);
      setReviews(reviewsRes.data);

      // Clear form fields
      setComment("");
      setRating(5);
    } catch (err) {
      console.error("Review submission error:", err);
      setError(err.response?.data?.error || "Error submitting review");
    }
  };

  if (error) {
    return (
      <Container className="py-4">
        <div className="alert alert-danger">{error}</div>
      </Container>
    );
  }

  if (!movie) {
    return (
      <Container className="py-4">
        <div>Loading movie details...</div>
      </Container>
    );
  }

  return (
    <Container className="py-4">
      <Row className="mb-4">
        <Col md={4}>
          <img
            src={`https://image.tmdb.org/t/p/w500${movie.posterPath}`}
            alt={movie.title}
            className="img-fluid rounded shadow"
          />
        </Col>
        <Col md={8}>
          <h1>{movie.title}</h1>
          <p className="lead">{movie.overview}</p>
          <div className="mb-3">
            <span className="fw-bold">Release Date:</span>{" "}
            {new Date(movie.releaseDate).toLocaleDateString()}
          </div>
        </Col>
      </Row>

      <Card className="mb-4">
        <Card.Body>
          <Card.Title>Write a Review</Card.Title>
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Rating</Form.Label>
              <Form.Select
                value={rating}
                onChange={(e) => setRating(Number(e.target.value))}
              >
                {[5, 4, 3, 2, 1].map((num) => (
                  <option key={num} value={num}>
                    {num} Stars
                  </option>
                ))}
              </Form.Select>
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Control
                as="textarea"
                rows={3}
                placeholder="Your review..."
                value={comment}
                onChange={(e) => setComment(e.target.value)}
              />
            </Form.Group>
            <Button variant="primary" onClick={submitReview}>
              Submit Review
            </Button>
          </Form>
        </Card.Body>
      </Card>

      <h3 className="mb-3">User Reviews</h3>
      {reviews.length === 0 ? (
        <div className="text-muted">
          No reviews yet. Be the first to write one!
        </div>
      ) : (
        reviews.map((review) => (
          <Card key={review._id} className="mb-3">
            <Card.Body>
              <div className="d-flex justify-content-between mb-2">
                <strong>{review.user?.username}</strong>
                <div>
                  {Array(review.rating)
                    .fill()
                    .map((_, i) => (
                      <span key={i}>⭐</span>
                    ))}
                </div>
              </div>
              <p>{review.comment}</p>
            </Card.Body>
          </Card>
        ))
      )}
    </Container>
  );
}
